    lookup.addParty(purchRFQTable.vendTable().Party);
    if (purchRFQTable.ContactPerson)
    {
        lookup.addParty(purchRFQTable.contactPerson().Party);
    }

    lookup.parmMethodType(LogisticsElectronicAddressMethodType::Email);
    lookup.performFormLookup();
}

