public boolean validate()
{
    boolean ret;

    ret = salesPurchOperationTypeController_BR.validate(purchRFQTable.SalesPurchOperationType_BR);

    ret = super() && ret;

    if (ret)
    {
        if (Box::yesNo("@GLS223104", DialogButton::No) == DialogButton::Yes)
        {
            purchRFQTable.write();
            PurchRFQTable::updateCFOPcodeInLines_BR(purchRFQTable.RecId);
            purchRFQTable_ds.reread();
            purchRFQTable_ds.reReadLines();
            purchRFQTable_ds.rereadReferenceDataSources();
            purchRFQTable_ds.refresh();
        }
        else
        {
            ret = false;
        }
    }

    return ret;
}

