private void setCFOP_BR(boolean _askToUser = false)
{
    CFOPValidation_BR           cfopValidation;

    cfopValidation       = CFOPValidation_BR::construct(purchRFQLine);
    purchRFQLine.cfopTable_BR    = cfopValidation.getDefaultCFOP(_askToUser);
    purchRFQLine.setTaxGroupAndTaxItemGroupFields_BR();
}

