/// </returns>
public boolean validateWrite()
{
    purchRFQLine.InventDimId    =   InventDim::findOrCreate(inventDim).InventDimId;
    purchRFQReplyLine.rfqId     =   purchRFQLine.rfqId;

    return super();
}

public void create(boolean _append = true)
{
    super(_append);

    newlyCreatedPurchRFQLine.data(purchRFQLine);
    element.setItemType(purchRFQLine.LineType, false);

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        purchRFQLine_ItemId.setFocus();
    }
    else
    {
        // </GBR>
        purchRFQLine_LineType.setFocus();
        // <GBR>
    }
    // </GBR>
}

public void executeQuery()
{
    logisticsPostalAddressLine_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    logisticsPostalAddressLine_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());

    super();
}

public void init()
{
    super();
    // Enable caching of the document handling and note it display fields
    purchRFQLine_ds.cacheAddMethod(tableMethodStr(PurchRFQLine, showDocHandIcon));

    if (formCtrl)
    {
        formCtrl.itemDataSourceInitPost(purchRFQLine);
    }
    this.query().dataSourceTable(tableNum(PurchRFQLine)).addOrderByField(fieldNum(PurchRFQLine,rfqCaseLineLineNumber), SortOrder::Ascending);
}

public void delete()
{
    if (element.getRFQLinesMarkForDelete())
    {
        element.deleteMarkedRecords();
    }
    else
    {
        // If no line is maked for delete then it will delete
        // the current line in seletion having status < Received
        super();
    }

    purchRFQTable_ds.reread();
    purchRFQTable_ds.refresh();
    purchRFQLine_ds.research();
    element.setButtonAccessLine();
}

public void initValue()
{
    if (purchRFQTable.RecId)
    {
        purchRFQLine.initFromPurchRFQTable(purchRFQTable);
    }

    super();
    purchRFQLine.Status = PurchRFQStatus::Received;
}

void itemIdModified()
{
    purchRFQLine.itemIdChanged();
    inventDim.data(InventDim::find(purchRFQLine.InventDimId));
    InventMovement::bufferSetTransQtyUnit(purchRFQLine);

    if (purchRFQLine.ProjId)
    {
        element.projInventJournalTransMapForm().fieldModifiedItemIdPost();
    }

    element.updateDesign(InventDimFormDesignUpdate::FieldChange);

    if (formCtrl)
    {
        formCtrl.fieldModified(fieldNum(PurchRFQLine, ItemId));
    }
}

public void write()
{
    PurchRFQStatus      oldStatus = purchRFQLine.Status;
    boolean             isNewLine;

    purchRFQLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;

    if (!purchRFQLine.RecId)
    {
        isNewLine = true;
    }

    // <GIN>
    if (TaxParameters::checkTaxParameters_IN())
    {
        if (purchRFQLine.RecId)
        {
            purchRFQLine.updateMRP_IN();
        }
    }
    // </GIN>
    super();

    // <GIN>
    if (purchRFQLineTaxExtensionIN_ds.isNeeded())
    {
        TaxModelDocLineExtensionController_IN::insertTaxExtensionRecord(purchRFQLineTaxExtensionIN, purchRFQLine);
    }
    // </GIN>
    if (purchRFQLine.InventDimId != inventDim.InventDimId)
    {
        inventDim.data(InventDim::find(purchRFQLine.InventDimId));
        inventDim_ds.setCurrent();
    }

    if (purchRFQLine.Status != oldStatus || isNewLine)
    {
        purchRFQTable_ds.reread();
        purchRFQTable_ds.refresh();
        purchRFQReplyLine_ds.reread();
        purchRFQReplyLine_ds.refresh();
        element.setButtonAccessHeader();
    }
}

public int active()
{
    int ret;

    element.setButtonAccessLine();

    element.setItemType(purchRFQLine.LineType, purchRFQLine.RecId? true : false);

    purchRFQTable_ds.allowDelete(purchRFQTable.StatusHigh < PurchRFQStatus::Received);
    purchRFQLine_ds.allowEdit(purchRFQLine.checkUpdate());
    purchRFQLine_ds.allowDelete(purchRFQLine.checkDelete());
    purchRFQLine_ds.allowCreate(purchRFQLine.checkUpdate() && (purchRFQTable.RFQType != PurchRFQType::PurchReq));
    inventDim_ds.allowEdit(purchRFQLine_ds.allowEdit());

    ret = super();

    element.updateDesign(InventDimFormDesignUpdate::Active);
    element.showLineLogisticsPostalEffectiveAddressMessage();

    if (tabLineDimension.isDisplayed())
    {
        tabLineDimension.arrange();
    }

    if (formCtrl)
    {
        formCtrl.dataSourceActivePost();
    }

    // Make the allow delete as true, if any one of the RFQLine with the status < Received is marked for delete and
    // if current line in selection has status as Received where delete is not allowed otherwise the default allowDelete() is called
    if (element.getRFQLinesMarkForDelete() &&
        !purchRFQLine_ds.allowDelete() &&
        purchRFQReplyLine.rfqMark == NoYes::Yes)
    {
        purchRFQLine_ds.allowDelete(true);
    }
    else
    {
        purchRFQLine_ds.allowDelete();
    }

    element.enableProjSalesPrice();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        element.enableServiceFields_BR();
    }
    // </GBR>

    if (Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Purchase))
    {
        PurchRFQLine_ds.object(fieldNum(PurchRFQLine, TaxGroup)).allowEdit(purchRFQLine.OverrideSalesTax == NoYes::Yes);
        PurchRFQLine_ds.object(fieldNum(PurchRFQLine, TaxItemGroup)).allowEdit(purchRFQLine.OverrideSalesTax == NoYes::Yes);
    }

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    HierarchyTreeTable::lookupHierarchyTree(_formControl, ProjTable::find(purchRFQLine.ProjId));
}

public void modified()
{
    super();

    if (purchRFQLine.ProjId)
    {
        element.projInventJournalTransMapForm().fieldModifiedActivityNumberPost();
    }
}

