public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common                      ret;
    CFOPAttributesDeterminer_BR cfopAttributes;

    if (BrazilParameters::isEnabled())
    {
        cfopAttributes     = CFOPAttributesDeterminer_BR::construct(purchRFQLine);
        ret = CFOPLookupHelper_BR::lookupCFOPTable(_formReferenceControl, cfopAttributes);
    }

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();
    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        ret = ret
            && CFOPValidation_BR::validateDeliveryAddress(
                InventSite::find(inventDim.InventSiteId).fiscalEstablishment(),
                purchRFQLine.DeliveryPostalAddress);
    }
    // </GBR>
    return ret;
}

public void modified()
{
    super();
    element.showLineLogisticsPostalEffectiveAddressMessage();
    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        purchRFQLine_ds.rereadReferenceDataSources();
    }
    // </GBR>
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common ret;
    CompanyInfo companyInfo = CompanyInfo::find();

    ret = LogisticsLocationSelectionLookup::lookupAddressForm(_formReferenceControl,
        companyInfo,
        [LogisticsLocationRoleType::Delivery],
        false,
        LogisticsLocationSelectionLookup::transactionAssociatedAddresses(
                                              tableNum(PurchRFQLine),
                                              fieldNum(PurchRFQLine, DeliveryPostalAddress),
                                              fieldNum(PurchRFQLine, rfqId),
                                              purchRFQTable.rfqId,
                                              purchRFQTable.DeliveryPostalAddress),
        purchRFQLine);

    return ret;
}

public void modified()
{
    ItemId          itemId;
    TaxGroup        taxGroup;
    PurchRFQStatus  status;

    if (!purchRFQLine.RecId)
    {
        itemId                  = purchRFQLine.ItemId;
        taxGroup                = purchRFQLine.TaxGroup;
        status                  = purchRFQLine.Status;
        purchRFQLine.data(newlyCreatedPurchRFQLine);
        purchRFQLine.ItemId     = itemId;
        purchRFQLine.TaxGroup   = taxGroup;
        purchRFQLine.Status     = status;
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        purchRFQLine.cfopTable_BR = 0;
        element.enableServiceFields_BR();
    }
    // </GBR>

    // <GIN>
    if (TaxParameters::isExciseEnable_IN())
    {
        purchRFQLine.SalesPurchJournalLineExtension_IN::initFromPrimaryTable(purchRFQLine);
        purchRFQLine_DS.refresh();
    }
    // </GIN>

    super();

    purchRFQLine_DS.itemIdModified();

    if (productFromLookup) // if there was product selected from the product lookup
    {
        element.updateProductDimensions(InventDim::find(productFromLookup.InventDimId));
        productFromLookup = null;
    }

    element.enableProjSalesPrice();
}

/// <summary>
/// Executing lookup
/// </summary>
/// <param name = "_formControl">Form control.</param>
/// <param name = "_filterStr">String to use in the lookup filter</param>
public void lookup(FormControl _formControl, str _filterStr)
{
    if (isProductSearchLookupEnabled)
    {
        EcoResProductSearchLookup::lookupProduct(_formControl as FormStringControl);
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

