public void lookup(FormControl _formControl, str _filterStr)
{
    LogisticsPostalAddress logisticsPostalAddress;

    logisticsPostalAddress = LogisticsPostalAddress::findRecId(purchRFQTable.DeliveryPostalAddress);
    TaxServiceCode_BR::lookup(_formControl,
                              purchRFQTable.ServiceCodeOnDlvAddress_BR,
                              logisticsPostalAddress.CountryRegionId,
                              logisticsPostalAddress.State,
                              logisticsPostalAddress.City);
}

