[SysClientCacheDataMethodAttribute(true)]
display PurchRFQReplyProgressStatus replyProgressStatus()
{
    return (purchRFQReplyTable.ReplyProgressStatus == PurchRFQReplyProgressStatus::VendorEditing
              && VendTable::find(purchRFQTable.VendAccount).VendVendorCollaborationType == VendVendorCollaborationType::Disabled)
            ? PurchRFQReplyProgressStatus::ReturnedToVendor
            : purchRFQReplyTable.ReplyProgressStatus;
}

public void modified()
{
    super();
    purchRFQTable.PaymentSched = PaymTerm::find(PurchRFQReplyTable.Payment).PaymSched;
}

public int active()
{
    int ret;

    element.setFieldsAccess();

    ret = super();

    return ret;
}

public void init()
{
    super();
    this.query().dataSourceTable(tableNum(PurchRFQReplyLine)).clearRange(fieldNum(PurchRFQReplyLine, rfqLineRecId));

    if (formCtrl)
    {
        formCtrl.dataSourceInitPost(purchRFQReplyLine);
    }
}

public void write()
{
    purchRFQReplyLine.LineNum       = purchRFQLine.LineNum;
    purchRFQReplyLine.rfqId         = purchRFQLine.rfqId;
    purchRFQReplyLine.ReplyReceived = NoYes::Yes;

    super();
}

public void modified()
{
    super();

    if (formCtrl)
    {
        formCtrl.fieldModified(fieldNum(PurchRFQReplyLine, PdsCWQty));
    }
}

public void initValue()
{
    super();
    purchRFQLineTaxExtensionIN.TaxModelDocLineExtensionIN::init(purchRFQLine);
}

public boolean isNeeded()
{
    #ISOCountryRegionCodes
    return SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]);
}

public void init()
{
    if (this.isNeeded())
    {
        this.linkType(FormLinkType::InnerJoin);
    }
    super();
}

public void write()
{
    super();

    purchRFQReplyTable_ds.reread();
    purchRFQReplyTable_ds.refresh();
}

public int active()
{
    int ret;

    ret = super();

    kmKnowledgeCollectorAnswerReportForm.enabled(element.existQuestionnaireResponse());

    return ret;
}

public void init()
{
    super();

    this.cacheAddMethod(tableMethodStr(PurchRFQTableKMCollection, displayDescription));
    this.cacheAddMethod(tableMethodStr(PurchRFQTableKMCollection, questionnaireStatus));
}

public void init()
{
    super();

    InventDistinctProductExpanded::prepareInventDistinctProductDatasource(InventDistinctProductExpanded_DisplayProductNumber,
                                                                          this.queryBuildDataSource(),
                                                                          PurchRFQLine_DS.name(),
                                                                          fieldNum(PurchRFQLine, ItemId),
                                                                          InventDim_DS.name());
}

public void clicked()
{
    super();
    element.viewEditModeHelper().setViewEditMode(ViewEditMode::Edit);
}

void clicked()
{
    //<GTE>
    if (TaxSolutionScopeIntegrationUtil::IsCompanyEnabled())
    {
        Common                      transactionTable = PurchRFQTable_ds.cursor();
        ClassId                     bundlerId = className2Id(classStr(TaxableDocumentDescriptorPurchaseRFQ));
        TaxableDocumentDescriptor   bundler = TaxableDocumentDescriptor::getTaxDocumentdescriptor(bundlerId, transactionTable);

        if (!bundler.skipTaxDocument())
        {
            ITaxableDocument taxableDocumentObject = TaxableDocumentObject::construct(bundler);
            TaxBusinessService::calculateTax(taxableDocumentObject, false);
        }
    }
    //</GTE>
    if (element.canProceed())
    {
        super();
    }
}

public void clicked()
{
    super();

    if (Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Purchase))
    {
        PurchRFQTable_ds.reread();
        PurchRFQTable_ds.reReadLines();
    }
}

public void clicked()
{
    this.parameters(classStr(TaxableDocumentDescriptorPurchaseRFQReply));
    super();
}

/// <summary>
/// This method is executed when Print Management button is clicked on the list page.
/// </summary>
/// <remarks>
/// Handles the print management for the list page.
/// </remarks>
void clicked()
{
    PrintMgmtSetupContext setupContext = new PrintMgmtSetupContext();

    if (purchRFQTable_ds.cursor() == null)
    {
        return;
    }

    super();

    setupContext.addHierarchyContext(PrintMgmtHierarchyType::Purch, PrintMgmtNodeType::PurchRFQTable);
    setupContext.parmCaller(element);
    setupContext.parmReferencedTableBuffer(purchRFQTable_ds.cursor());
    setupContext.parmCountryRegionId(purchRFQTable.vendTable().postalAddress().CountryRegionId);
    PrintMgmt::launchSetup(setupContext);
}

