[Form]
public class PurchRFQVendQuestionAnswer extends FormRun
{
    private boolean isCurrentUserProcurementAgent;
    private PurchRFQVendQuestionAnswerFormHelper purchRFQVendQuestionAnswerFormHelper;

    private QueryBuildRange range;
    private QueryBuildRange rangePublishedQuestion;
    private QueryBuildDataSource buildDataSourcePublishAnswer;
    private Query query;

    private PurchRFQId rfqId;
    private PurchRFQCaseId rfqCaseId;

}

/// <summary>
/// Initializes and constructs the form helper class and user role.
/// </summary>
[Hookable(false)]
public void init()
{
    isCurrentUserProcurementAgent = xUserInfo::doesCurrentUserHaveProcurementAgentRights();

    super();

    purchRFQVendQuestionAnswerFormHelper = PurchRFQVendQuestionAnswerFormHelper::newFromParameters(isCurrentUserProcurementAgent,
                                                                                    element,
                                                                                    purchRFQVendPostedQuestionAnswer,
                                                                                    purchRFQVendPostedQuestionAnswer_ds);

    VendorName.visible(isCurrentUserProcurementAgent);
    VendorAccount.visible(isCurrentUserProcurementAgent);

    #SysSystemDefinedButtons
    FormCommandButtonControl newButton = element.control(element.controlId(#SystemDefinedNewButton)) as FormCommandButtonControl;
    newButton.text('@SCM:AskNewQuestionButtonLabel');

    if (element.args().caller().name() == formStr(PurchRFQVendReply)
        || element.args().caller().name() == formStr(PurchRFQSealedVendReply))
    {
        PurchRFQTableRecId rfqTableRecId = FormDataUtil::getFormDataSource(element.args().record()).formRun().dataSource(1).cursor().RecId;
        PurchRFQTable rfqTable = PurchRFQTable::findRecId(rfqTableRecId);
        rfqId = rfqTable.RFQId;
        rfqCaseId = rfqTable.RFQCaseId;

        FormStringControl vendorQuestionCutOffDate = element.control(element.controlId(formControlStr(PurchRFQVendQuestionAnswer, VendorQuestionCutoffDateTime))) as FormStringControl;
        vendorQuestionCutOffDate.text(datetime2Str(DateTimeUtil::applyTimeZoneOffset(PurchRFQCaseTable::find(rfqCaseId).VendorQuestionCutoffDateTime, DateTimeUtil::getUserPreferredTimeZone())));
    }
}

/// <summary>
/// Creates new record in <c>PurchRFQVendPostedQuestionAnswer</c> table.
/// </summary>
/// <param name = "_append">A boolean value that indicate whether to insert record after or before current record.</param>
[Hookable(false)]
public void create(boolean _append = false)
{
    if (!PurchRFQVendPostedQuestionAnswer.RecId)
    {
        PurchRFQVendPostedQuestionAnswer_ds.leaveRecord();
    }

    if (purchRFQVendQuestionAnswerFormHelper.create(this.formRun()))
    {
        super(_append);
    }
    else
    {
        if (element.args().caller()
            && element.args().parmEnum() == FormOpenMode::ForNew)
        {
            element.close();
        }
    }

    purchRFQQuestionNotPublishedVendorView_ds.executeQuery();
    purchRFQVendPostedQuestionAnswer_ds.executeQuery();
}

[Hookable(false)]
public void init()
{
    super();

    if (element.args().caller().name() == formStr(PurchRFQVendReply) && !isCurrentUserProcurementAgent)
    {
        query = this.query();
        buildDataSourcePublishAnswer = query.dataSourceTable(tableNum(PurchRFQVendPostedQuestionAnswer));
        range = SysQuery::findOrCreateRange(buildDataSourcePublishAnswer, fieldNum(PurchRFQVendPostedQuestionAnswer, DataAreaId));
    }
}

[Hookable(false)]
public int active()
{
    int ret = super();
    boolean isEditAllowed = true;

    purchRFQVendPostedGroupQuestionAnswer_ds.research();

    if (strLTrim(strRTrim(purchRFQVendPostedGroupQuestionAnswer_ds.object(fieldNum(PurchRFQVendPostedGroupQuestionAnswer, GroupCode)).getValue())) == '')
    {
        purchRFQVendPostedGroupQuestionAnswer_ds.object(fieldNum(PurchRFQVendPostedGroupQuestionAnswer, GroupQuestion)).setValue('');
        purchRFQVendPostedGroupQuestionAnswer_ds.object(fieldNum(PurchRFQVendPostedGroupQuestionAnswer, GroupAnswer)).setValue('');
        isEditAllowed = false;
    }

    purchRFQVendPostedGroupQuestionAnswer_ds.object(fieldNum(PurchRFQVendPostedGroupQuestionAnswer, GroupQuestion)).allowEdit(isEditAllowed);
    purchRFQVendPostedGroupQuestionAnswer_ds.object(fieldNum(PurchRFQVendPostedGroupQuestionAnswer, GroupAnswer)).allowEdit(isEditAllowed);

    return ret;
}

[Hookable(false)]
public boolean validateWrite()
{
    purchRFQVendPostedGroupQuestionAnswer.RFQCaseId = purchRFQVendPostedQuestionAnswer.RFQCaseId;

    purchRFQVendPostedGroupQuestionAnswer_ds.object(fieldNum(PurchRFQVendPostedGroupQuestionAnswer, GroupCode)).mandatory(false);

    return super();
}

[Hookable(false)]
public void write()
{
    if (purchRFQVendPostedGroupQuestionAnswer_ds.validateWrite())
    {
        PurchRFQVendPostedGroupQuestionAnswer postedGroupQuestionAnswer = PurchRFQVendPostedGroupQuestionAnswer::findByRFQCaseIdGroupCode(purchRFQVendPostedGroupQuestionAnswer.RFQCaseId, purchRFQVendPostedGroupQuestionAnswer.GroupCode);

        if (!postedGroupQuestionAnswer
            && purchRFQVendPostedGroupQuestionAnswer.GroupCode != '')
        {
            purchRFQVendPostedGroupQuestionAnswer.insert();
            purchRFQVendPostedQuestionAnswer.PurchRFQVendPostedGroupQuestionAnswerRecId = purchRFQVendPostedGroupQuestionAnswer.RecId;
            purchRFQVendPostedQuestionAnswer.update();
        }
        else
        {
            // This condition is to prevent the group question and group answer being overridden by a blank value
            // when an existing group code is assigned to other questions on the grid.
            if (postedGroupQuestionAnswer
                && ((postedGroupQuestionAnswer.GroupQuestion == ''
                      && postedGroupQuestionAnswer.GroupAnswer == '')
                    || purchRFQVendPostedQuestionAnswer.IsAnswerPublishedToVendor))
            {
                ttsbegin;

                postedGroupQuestionAnswer.selectForUpdate(true);
                postedGroupQuestionAnswer.GroupQuestion = purchRFQVendPostedGroupQuestionAnswer.GroupQuestion;
                postedGroupQuestionAnswer.GroupAnswer = purchRFQVendPostedGroupQuestionAnswer.GroupAnswer;
                postedGroupQuestionAnswer.update();

                ttscommit;
            }

            // To update the other questions on the grid that get an existing group code.
            purchRFQVendPostedQuestionAnswer.PurchRFQVendPostedGroupQuestionAnswerRecId = postedGroupQuestionAnswer.RecId;
            purchRFQVendPostedQuestionAnswer.update();
        }
    }

    // Commented so that it does not attempt to create a new record when same group code is added to a new question.
    //super();
}

[Hookable(false)]
public void executeQuery()
{
    Query queryVendorView = purchRFQQuestionNotPublishedVendorView_ds.query();
    QueryBuildDataSource queryDataSourceVendorView = queryVendorView.dataSourceTable(tableNum(PurchRFQVendPostedQuestionAnswer));

    queryDataSourceVendorView.addRange(fieldNum(PurchRFQVendPostedQuestionAnswer, RFQId)).value(queryValue(rfqId));
    queryDataSourceVendorView.addRange(fieldNum(PurchRFQVendPostedQuestionAnswer, IsAnswerPublishedToVendor)).value(queryValue(NoYes::No));

    super();
}

[Hookable(false)]
public void init()
{
    super();

    if (element.args().caller().name() == formStr(PurchRFQVendReply)
        || element.args().caller().name() == formStr(PurchRFQSealedVendReply))
    {
        PurchRFQTableRecId rfqTableRecId = FormDataUtil::getFormDataSource(element.args().record()).formRun().dataSource(1).cursor().RecId;
        PurchRFQTable rfqTable = PurchRFQTable::findRecId(rfqTableRecId);
        rfqId = rfqTable.RFQId;
        rfqCaseId = rfqTable.RFQCaseId;

        PurchRFQVendTmpPublishedQuestionAnswer.linkPhysicalTableInstance(PurchRFQVendQuestionAnswerManager::populateTmpData(rfqId, rfqCaseId, purchRFQVendPostedQuestionAnswer));
    }
}

[Hookable(false)]
public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        purchRFQVendPostedGroupQuestionAnswer_ds.active();

        PurchRFQVendPostedQuestionAnswer localPostedQuestionAnswer = PurchRFQVendQuestionAnswerManager::isExistingGroupCodeAlreadyPublished((PurchRFQVendPostedGroupQuestionAnswer::findByRFQCaseIdGroupCode(
        PurchRFQVendPostedQuestionAnswer.RFQCaseId, purchRFQVendPostedGroupQuestionAnswer.GroupCode)).RecId);
        if (localPostedQuestionAnswer.RecId)
        {
            purchRFQVendPostedQuestionAnswer.IsDirectResponse = localPostedQuestionAnswer.IsDirectResponse;
        }

        purchRFQVendPostedQuestionAnswer_ds.object(fieldNum(PurchRFQVendPostedQuestionAnswer, IsDirectResponse)).allowEdit(!localPostedQuestionAnswer.RecId);
    }

    return ret;
}

[Hookable(false)]
public boolean validate()
{
    boolean ret = super();

    if (ret
        && strLRTrim(purchRFQVendPostedGroupQuestionAnswer.GroupCode) != ''
        && strLRTrim(PurchRFQVendPostedQuestionAnswer.CustomerAnswer) != '')
    {
        ret = checkFailed('@SCM:PurchRFQVendAnswerGroupAnswerOnSameLineError');
    }

    return ret;
}

[Hookable(false)]
public boolean modified()
{
    boolean ret = super();

    if (ret
        && strLRTrim(purchRFQVendPostedGroupQuestionAnswer.GroupQuestion) != ''
        && !purchRFQVendPostedQuestionAnswer.IsAnswerPublishedToVendor
        && PurchRFQVendPostedGroupQuestionAnswer::findByRFQCaseIdGroupCode(purchRFQVendPostedQuestionAnswer.RFQCaseId, purchRFQVendPostedGroupQuestionAnswer.GroupCode).GroupQuestion != '')
    {
        ret = checkFailed('@SCM:PurchRFQVendMultipleQuestionsForGroupError');
    }

    return ret;
}

[Hookable(false)]
public boolean modified()
{
    boolean ret = super();

    if (ret
        && strLRTrim(purchRFQVendPostedGroupQuestionAnswer.GroupAnswer) != ''
        && !purchRFQVendPostedQuestionAnswer.IsAnswerPublishedToVendor
        && PurchRFQVendPostedGroupQuestionAnswer::findByRFQCaseIdGroupCode(purchRFQVendPostedQuestionAnswer.RFQCaseId, purchRFQVendPostedGroupQuestionAnswer.GroupCode).GroupAnswer != '')
    {
        ret = checkFailed('@SCM:PurchRFQVendMultipleQuestionsForGroupError');
    }

    return ret;
}

[Hookable(false)]
public boolean modified()
{
    boolean ret = super();

    if (ret
        && strLRTrim(purchRFQVendPostedGroupQuestionAnswer.GroupCode) != ''
        && strLRTrim(PurchRFQVendPostedQuestionAnswer.CustomerAnswer) != '')
    {
        ret = checkFailed('@SCM:PurchRFQVendAnswerGroupAnswerOnSameLineError');
