public class PurchRFQVendReply extends FormRun implements SysIFilterConsumerForm, SysIFilterEventHandler
{
    private boolean callWithRecord;
    private boolean showingFilteredList;
    private boolean isCurrentUserProcurementAgent;
    private boolean isEditAllowed;

    private Constant editTask = 4471;

    private PurchRFQVendReplyFormHelper purchRFQVendReplyFormHelper;

}

/// <summary>
/// Register the data source to which the filter should be applied.
/// </summary>
public void registerDatasourceOnQueryingEvent()
{
    PurchRFQTable_DS.OnQueryExecuting += eventhandler(this.parmFilterFormQueryEventHandler().applyFilter);
    showingFilteredList = true;
}

/// <summary>
/// Event handler to react on a filter change.
/// </summary>
public void onFilterChanged()
{
    PurchRFQTable_DS.executeQuery();
}

/// <summary>
/// Determine whether changing the reply fields value should result in status change.
/// </summary>
/// <returns>
/// true if changing the reply fields value should result in status change, otherwise false.
/// </returns>
/// <remarks>
/// This method is called by the table to determine if updating the fields should result in status change.
/// </remarks>
public boolean shouldUpdateRFQStatus()
{
    return false;
}

private void openVendorBidForm(PurchRFQTable _purchRFQTable)
{
    Args args = new Args();
    args.record(_purchRFQTable);
    var vendReplyMenu = new MenuFunction(menuItemDisplayStr(PurchRFQVendReply), MenuItemType::Display);
    vendReplyMenu.openMode(ViewEditMode::Edit);
    vendReplyMenu.run(args);
}

private void showDetails(PurchRFQId _purchRFQId)
{
    if (showingFilteredList)
    {
        element.openVendorBidForm(PurchRFQTable::find(_purchRFQId));
    }
    else
    {
        element.viewOptionHelper().setViewOption(FormViewOption::Details);
        element.viewEditModeHelper().setViewEditMode(ViewEditMode::Edit);
    }

    PurchRFQTable_ds.research(true);
}

private void showSealedBidForm(PurchRFQTable _purchRFQTable)
{
    PurchRFQSealedBidFlightingManager::validatePreviewIsEnabled();

    Args args = new Args();
    args.caller(element);
    args.record(_purchRFQTable);

    var sealedVendReply = new MenuFunction(formStr(PurchRFQSealedVendReply), MenuItemType::Display);
    sealedVendReply.openMode(ViewEditMode::Edit);
    sealedVendReply.run(args);

    PurchRFQTable_ds.research(true);
}

public int task(int _p1)
{
    int ret = super(_p1);

    if (_p1 == editTask)
    {
        if (purchRFQTable.VendReplyDate
            && purchRFQTable.StatusLow == PurchRFQStatus::Sent
            && !purchRFQCaseTable.isRFQExpiredForCurrentUser())
        {
            purchRFQTable.resetVendReplyDateTime();
            element.showDetails(PurchRFQTable.RFQId);
        }

        element.viewOptionHelper().setViewOption(FormViewOption::Details);
    }

    return ret;
}

public void init()
{
    VendVendorPortalUserRequestManager::validateVendorPortalUserAccessToAtLeastOneVendor(this);

    isCurrentUserProcurementAgent = xUserInfo::doesCurrentUserHaveProcurementAgentRights();

    super();
    purchRFQVendReplyFormHelper = PurchRFQVendReplyFormHelper::construct(isCurrentUserProcurementAgent);
    purchRFQVendReplyFormHelper.init(
        element,
        purchRFQReplyTable,
        purchRFQCaseTable,
        purchRFQTable,
        purchRFQReplyTable_ds,
        purchRFQReplyLine_ds);
}

public boolean closed()
{
    boolean ret = super();

    if (ret && this.args() != null && this.args().parmObject() != null)
    {
        FormRun callerForm = this.args().parmObject();
        callerForm.dataSource().research(true);
    }

    return ret;
}

