}

public void init()
{
    super();

    this.query().dataSourceTable(tableNum(PurchRFQLine)).addRange(fieldNum(PurchRFQLine, Status))
        .value(strFmt('(%2 == %1) || (%3 != 0) ',
            enum2int(PurchRFQStatus::Accepted),
            fieldId2Name(tableNum(PurchRFQLine), fieldNum(PurchRFQLine, Status)),
            fieldId2Name(tableNum(PurchRFQLine), fieldNum(PurchRFQLine, RFQCaseLineLineNumber))));
}

public void executeQuery()
{
    super();
    purchRFQVendReplyFormHelper.updateDesign(
        purchRFQReplyTable,
        purchRFQCaseTable,
        purchRFQTable,
        purchRFQReplyTable_ds,
        purchRFQReplyLine_ds);
}

public void write()
{
    ttsbegin;

    if (purchRFQReplyTable.existLine() && (purchRFQReplyTable.DeliveryDate != purchRFQReplyTable.orig().DeliveryDate))
    {
        DialogButton copyDeliveryDate = Box::yesYesAllNoCancel("@SCM:PurchRFQReplyUpdateDeliveryDateOnLines", DialogButton::Yes);

        if (copyDeliveryDate != DialogButton::Cancel && copyDeliveryDate != DialogButton::No)
        {
            PurchRFQReplyLine localPurchRFQReplyLine;

            update_recordset localPurchRFQReplyLine
                setting DeliveryDate = purchRFQReplyTable.DeliveryDate
                where localPurchRFQReplyLine.RFQId == purchRFQReplyTable.RFQId
                   && (copyDeliveryDate == DialogButton::YesToAll
                      || localPurchRFQReplyLine.DeliveryDate == purchRFQReplyTable.orig().DeliveryDate);
        }
    }

    super();

    ttscommit;
}

public boolean validateWrite()
{
    // Fields might not have been selected on the specified buffers, or might have been updated since selection
    inventDim.checkInvalidFieldAccess(false);

    return super();
}

