/// <summary>
/// Format entered category name filter and add the '*' to support partial search.
/// </summary>
/// <param name = "_categoryNameFilter">Category name filter entered by user.</param>
public void categoryNameFilterModified(str _categoryNameFilter)
{
    str formattedCategoryNameFilter = '*' + _categoryNameFilter + '*';
    if (ecoResCategoryTreeDatasource)
    {
        ecoResCategoryTreeDatasource.setNameFilter(formattedCategoryNameFilter);
        ecoResCategoryTreeDatasource.setFocusOnFirstMatch();
    }
}

/// <summary>
/// Sets the procurement category tree control data source.
/// </summary>
/// <param name = "_root">Root category node.</param>
private void setProcurementCategory(EcoResCategory _root)
{ 
    EcoResCategoryLookupParameters lookupParameters = EcoResCategory::getLookupParameters(CompanyInfo::findDataArea(DataArea.text()).RecId,
                                                                                        this.getReceivingOperatingUnit());

    ecoResCategoryTreeDatasource = EcoResCategoryTreeDatasource::newFromParameter(ecoResCategory_ds,
                                                    hierarchyTreeCtrl,
                                                    fieldNum(EcoResCategory, RecId),
                                                    fieldNum(EcoResCategory, ParentCategory),
                                                    fieldNum(EcoResCategory, Name),
                                                    false, // No ID in description
                                                    true,
                                                    fieldNum(EcoResCategory, Code),
                                                    procurementCategoryHierarchy,
                                                    null,
                                                    lookupParameters,
                                                    fieldNum(EcoResCategory, NestedSetLeft) // for tree sort order
                                                    );

    purchReqAddItemForm.setProcurementCategoryForRoot(_root, ecoResCategoryTreeDatasource);
}

private RefRecId getReceivingOperatingUnit()
{
    RefRecId receivingOperatingUnit;
    if (nonCatalogItem.ReceivingOperatingUnit)
    {
        receivingOperatingUnit = nonCatalogItem.ReceivingOperatingUnit;
    }
    else
    {
        receivingOperatingUnit = HcmWorkerHelper::getPrimaryDepartmentRecId(nonCatalogItem.Requisitioner
                                                                            ? nonCatalogItem.Requisitioner
                                                                            : callerPurchReqTableRecord.Originator);
    }
    return receivingOperatingUnit;
}

/// <summary>
/// Adds a non-catalog line to selected lines with the mandatory fields set.
/// </summary>
/// <param name = "_mandatoryFieldsCon">Container holding the unlisted product name and unit of measure.</param>
private void addNonCatalogItem(container _mandatoryFieldsCon)
{
    this.setCommonFields();

    nonCatalogItem.BuyingLegalEntity = CompanyInfo::findDataArea(dataArea.text()).RecId;

    //  Once category has been filled in for the non-catalog item,
    //  all the relevant fields should be defaulted
    itemBasket.initNonCatalogItemWithExplicitMandatoryFields(nonCatalogItem, lineNum, _mandatoryFieldsCon);

    // insert the record into temporary table
    ItemBasket.doInsert();

    // create a new record for the non-catalog data source
    nonCatalogItem_ds.create(false);

    ItemBasket_ds.research(false);
}

private void setCommonFields()
{
    lineNum++;
    sequenceNumber++;

    itemBasket.clear();
    itemBasket.initFromPurchReqTable(callerPurchReqTableRecord);

    // we keep a link from item basket to a record in the temporary source document line
    this.setSourceDocumentLineRelation();
}

private void setSourceDocumentLineRelation()
{
    sourceDocumentLine.clear();
    sourceDocumentLine.SourceRelationType   = tableNum(PurchReqLine);
    sourceDocumentLine.SourceDocumentHeader = callerPurchReqTableRecord.SourceDocumentHeader;
    sourcedocumentLine.insert();

    itemBasket.SourceDocumentLine = sourceDocumentLine.RecId;
}

private void addCatalogItem()
{
    PurchReqLineValidate    purchReqLineValidate;
    InventDimId             currentInventDimId;

    this.setCommonFields();
    this.setInventDimIdForItemBasket();

    itemBasket.Requisitioner            = nonCatalogItem.Requisitioner;
    itemBasket.BuyingLegalEntity        = CompanyInfo::findDataArea(dataArea.text()).RecId;
    itemBasket.ReceivingOperatingUnit   = nonCatalogItem.ReceivingOperatingUnit;
    itemBasket.Attention                = HcmWorkerTitle::findByWorker(itemBasket.Requisitioner).OfficeLocation;

    itemBasket.initCatalogItem(InventTable::find(catalogItem.ItemId), lineNum, sequenceNumber, currencyCode);

    purchReqLineValidate = PurchReqLineValidate::newPurchReqLineValidate(itemBasket);

    if (purchReqLineValidate.checkItemId())
    {
        itemBasket.doInsert();
    }

    itemBasket_ds.research(false);
}

public void closeOk()
{
    if (!itemBasket.RecId)
    {
        if (Box::okCancel("@SYS344062", DialogButton::Cancel) == DialogButton::Cancel)
        {
            return;
        }
    }

    purchReqAddItemForm.parmPurchReqLineDS(itemBasket_ds);

    if (callerPurchReqTableRecord.checkUpdate())
    {
        purchReqAddItemForm.closeOK();

        super();

        if (callerFormRun)
        {
            Object localCallerFormRun = callerFormRun;
            localCallerFormRun.rereadLines();
            callerFormRun.updateWorkflowControls();
        }
    }
    else
    {
        // Items can not be added because purchase requisition status was changed.
        throw error("@SYS113058");
    }
}

