private void setInventDimIdForItemBasket()
{
    InventDimId   siteAndLocationInventDim;

    if (!isConsumption)
    {
        siteAndLocationInventDim = this.getSiteAndLocationInventDimId(inventDim);
    }

    if (siteAndLocationInventDim)
    {
        itemBasket.InventDimId = siteAndLocationInventDim;
    }
    else if (callerSiteAndLocationInventDimId)
    {
        itemBasket.InventDimId = callerSiteAndLocationInventDimId;
    }
}

private InventDimId getSiteAndLocationInventDimId(InventDim _inventDim)
{
    InventDim   siteAndLocationInventDim;
    InventDimId inventDimId;

    if (_inventDim.RecId)
    {
        siteAndLocationInventDim.InventSiteId     = _inventDim.InventSiteId;
        siteAndLocationInventDim.InventLocationId = _inventDim.InventLocationId;

        inventDimId = InventDim::findOrCreate(siteAndLocationInventDim).InventDimId;
    }

    return inventDimId;
}

