public void init()
{
    if (!element.args())
    {
        throw error("@SYS22539");
    }
    switch (element.args().dataset())
    {
        case  tableNum(PurchReqTable):
            callerPurchReqTableRecord = element.args().record();
            break;

        case  tableNum(PurchReqLine):
            callerPurchReqLineRecord  = element.args().record();
            callerPurchReqTableRecord = PurchReqTable::find(callerPurchReqLineRecord.PurchReqTable);
            break;
        default:
            throw error("@SYS22539");
    }

    isConsumption = callerPurchReqTableRecord.isConsumption();
    this.initCallerSiteAndLocationInventDimId();

    super();
    this.hideControls();

    if (callerPurchReqTableRecord.CompanyInfoDefault)
    {
        dataArea.text(CompanyInfo::findRecId(callerPurchReqTableRecord.CompanyInfoDefault).DataArea);
    }
    else
    {
        boolean isCurrentLegalEntityValid = confind(HcmWorkerHelper::getLegalEntityRecIds(callerPurchReqTableRecord.Originator), CompanyInfo::current());
        CompanyInfo selectedCompanyInfo = isCurrentLegalEntityValid ? CompanyInfo::findRecId(CompanyInfo::current()) : HcmWorkerHelper::getLegalEntity(callerPurchReqTableRecord.Originator);
        dataArea.text(selectedCompanyInfo.DataArea);
    }
    element.setDefaultCompany(element.selectedDataAreaId());

    callerFormRun = element.args().caller() as xFormRun;

    element.updateDesign(InventDimFormDesignUpdate::Init);

    purchReqAddItemForm = PurchReqAddItemForm::newPurchReqAddItemForm(callerPurchReqTableRecord);

    element.initCurrencyCode();
    element.setProcurementCategoryHierarchy();

    purchReqInternalOrgManager_LegalEntity   = new PurchReqInternalOrgManager_LegalEntity();
    purchReqInternalOrgManager_OperatingUnit = new PurchReqInternalOrgManager_OperatingUnit();

    this.setupFallbackProductTranslation();
}

private void setupFallbackProductTranslation()
{
    List fallbackStringControls = new List(Types::Integer);
    List ecoResProductTranslationStringControls = new List(Types::Integer);

    ecoResProductTranslationStringControls.addEnd(element.controlId(formControlStr(PurchReqAddItem, itemName)));
    fallbackStringControls.addEnd(element.controlId(formControlStr(PurchReqAddItem, EcoResProductTranslationFallbackView_Name)));

    EcoResProductTranslationFallbackViewModel::setupFallbackTranslationForm(element,
                                                                            element.EcoResProductTranslationFallbackView_ds,
                                                                            element.EcoResProduct_ds,
                                                                            fallbackStringControls,
                                                                            ecoResProductTranslationStringControls);
}

