void removeLineFromItemBasket()
{
    Common common;

    common = getFirstSelection(itemBasket_ds);

    ttsbegin;
    while (common)
    {
        itemBasket_ds.positionToRecord(common);
        purchReqAddItemForm.deleteVendorProposalSuggestion(itemBasket.RecId);
        itemBasket.doDelete();
        common =itemBasket_ds.getNext();
    }
    ttscommit;
    itemBasket_ds.research(false);
}

