void setFieldAccessBasket()
{
    boolean editNonCatalog = itemBasket.LineType == PurchReqLineType::Category;

    grid_Name.allowEdit(editNonCatalog);
    itemBasket_ds.object(fieldNum(PurchReqLine, PurchPrice)).allowEdit(editNonCatalog);
    itemBasket_ds.object(fieldNum(PurchReqLine, PurchUnitOfMeasure)).allowEdit(isConsumption && editNonCatalog);
    itemBasket_ds.object(fieldNum(PurchReqLine, LineAmount)).allowEdit(editNonCatalog);

    if (!itemBasket.isVendAccountEnabled())
    {
        itemBasket_ds.object(fieldNum(PurchReqLine,VendAccount)).allowEdit(false);
    }

    projectDimensions.visible(itemBasket.ProjId ? true : false);

    // Enable the Remove button if there is an active record in the basket
    buttonRemoveItemCatalog.enabled(itemBasket.RecId);
    buttonVendorSuggestions.enabled(editNonCatalog);

    TabDetails_ExternalItemId.visible(editNonCatalog);
    TabDetails_URL.visible(editNonCatalog);
}

public void setFilterValuesFromRequisitioner(HcmWorkerRecId _requisitionerRecId)
{
    if (callerPurchReqTableRecord.CompanyInfoDefault)
    {
        dataArea.text(CompanyInfo::findRecId(callerPurchReqTableRecord.CompanyInfoDefault).DataArea);
    }
    else
    {
        boolean isCurrentLegalEntityValid;
        CompanyInfo selectedCompanyInfo;

        if (PurchReqAddItemSetFilterValueFromRequisitionerFlight::instance().isEnabled())
        {
            isCurrentLegalEntityValid = confind(HcmWorkerHelper::getLegalEntityRecIds(_requisitionerRecId), CompanyInfo::current());
            selectedCompanyInfo = isCurrentLegalEntityValid ? CompanyInfo::findRecId(CompanyInfo::current()) : HcmWorkerHelper::getLegalEntity(_requisitionerRecId);
        }
        else
        {
            isCurrentLegalEntityValid = confind(HcmWorkerHelper::getLegalEntityRecIds(callerPurchReqTableRecord.Originator), CompanyInfo::current());
            selectedCompanyInfo = isCurrentLegalEntityValid ? CompanyInfo::findRecId(CompanyInfo::current()) : HcmWorkerHelper::getLegalEntity(callerPurchReqTableRecord.Originator);
        }

        dataArea.text(selectedCompanyInfo.DataArea);
    }

    element.setDefaultCompany(dataArea.text());

    nonCatalogItem.ReceivingOperatingUnit = HcmWorkerHelper::getDepartments(_requisitionerRecId).RecId;
    nonCatalogItem_ds.rereadReferenceDataSources();
}

