}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_PurchReq::newFromForm(element);
                inventDimFormSetup.parmRequisitionPurpose(callerPurchReqTableRecord.RequisitionPurpose);
            }
            break;

        case InventDimFormDesignUpdate::Active, InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(itemBasket.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;

        default :
            // Incorrect parameter for the function '%1'
            throw error(strFmt("@SYS54195", funcName()));
    }
}

void writeTmpFrmVirtual(TmpFrmVirtual _tmpFrmVirtual, TableId _tableId, RecId _recId, Num _id, LineNum _lineNum = 0, ItemId _itemId = '', InventDimId _inventDimId = '', Qty _qty = 0)
