public void init()
{
    QueryBuildDataSource    qbdsCatalogItem;
    QueryBuildDataSource    qbdsInventModelGroupItem;
    QueryBuildDataSource    qbdsInventModelGroup;
    QueryBuildRange         qbr;

    super();

    if (!element.isConsumption())
    {
        qbdsCatalogItem = this.queryBuildDataSource();
        qbdsInventModelGroupItem = qbdsCatalogItem.addDataSource(tableNum(InventModelGroupItem));
        qbdsInventModelGroupItem.relations(true);
        qbdsInventModelGroupItem.joinMode(JoinMode::ExistsJoin);

        qbdsInventModelGroup = qbdsInventModelGroupItem.addDataSource(tableNum(InventModelGroup));
        qbdsInventModelGroup.relations(true);
        qbdsInventModelGroup.joinMode(JoinMode::ExistsJoin);
        qbr = qbdsInventModelGroup.addRange(fieldNum(InventModelGroup, StockedProduct));
        qbr.value(SysQuery::value(NoYes::Yes));
    }
}

public void addCatalogPolicyRule()
{
    RefRecId buyingLegalEntity = CompanyInfo::findDataArea(dataArea.text()).RecId;

    PurchReqLine::addCatalogPolicyRuleToCatalogQuery(this.query(),
