public void addCategoryAccesssPolicyRule()
{
    ProcCategoryTmpPolicyRule   procCategoryTmpPolicyRule;
    QueryBuildDataSource        qbdsProcCategoryAccessPolicyParameter, qbdsUsedAccessPolicyData;
    str                         categoryRules = queryValue(0);
    boolean                     found = false;
    TableId                     tableIdParameter;
    FieldId                     fieldIdCategory, fieldIdAccessPolicyRule;
    SysPolicyRuleTypeEnum       sysPolicyRuleTypeEnum;
    RefRecId                    buyingLegalEntity = CompanyInfo::findDataArea(dataArea.text()).RecId;

    if (!element.isConsumption() && RequisitionReplenishCatAccessPolicyRule::findForLegalEntity(buyingLegalEntity).RecId)
    {
        tableIdParameter        = tableNum(RequisitionRepCatAccessPolicyRuleData);
        fieldIdCategory         = fieldNum(RequisitionRepCatAccessPolicyRuleData, Category);
        fieldIdAccessPolicyRule = fieldNum(RequisitionRepCatAccessPolicyRuleData, RequisitionReplenishCatAccessPolicyRule);
        sysPolicyRuleTypeEnum   = SysPolicyRuleTypeEnum::RequisitionReplenishCatAccessPolicyRule;
    }
    if (!tableIdParameter)
    {
        tableIdParameter        = tableNum(ProcCategoryAccessPolicyParameter);
        fieldIdCategory         = fieldNum(ProcCategoryAccessPolicyParameter, ProcurementCategory);
        fieldIdAccessPolicyRule = fieldNum(ProcCategoryAccessPolicyParameter, ProcurementCategoryAccessPolicyRule);
        sysPolicyRuleTypeEnum = SysPolicyRuleTypeEnum::CategoryAccessPolicyRule;
    }

    procCategoryTmpPolicyRule = EcoResCategory::getProcCategoryRules(sysPolicyRuleTypeEnum,
                                                                   buyingLegalEntity,
                                                                   nonCatalogItem.ReceivingOperatingUnit);
    while select procCategoryTmpPolicyRule
    {
        found = true;
        categoryRules += strFmt(',%1',  queryValue(procCategoryTmpPolicyRule.PolicyRule));
    }

    if (usedAccessPolicyDataTableId && usedAccessPolicyDataTableId != tableIdParameter)
    {
        // Do not use old data source.
        qbdsUsedAccessPolicyData = this.query().dataSourceTable(usedAccessPolicyDataTableId);
        if (qbdsUsedAccessPolicyData)
        {
            qbdsUsedAccessPolicyData.enabled(false);
            qbdsUsedAccessPolicyData.clearRanges();
        }
    }

    if (found)
    {
        qbdsProcCategoryAccessPolicyParameter = this.query().dataSourceTable(tableIdParameter);

        if (!qbdsProcCategoryAccessPolicyParameter)
        {
            qbdsProcCategoryAccessPolicyParameter = this.query().dataSourceTable(tableNum(EcoResCategory)).addDataSource(tableIdParameter);
            qbdsProcCategoryAccessPolicyParameter.addLink(fieldNum(EcoResCategory, RecId), fieldIdCategory);
            qbdsProcCategoryAccessPolicyParameter.joinMode(JoinMode::InnerJoin);
        }
        else
        {
            qbdsProcCategoryAccessPolicyParameter.enabled(true);
            qbdsProcCategoryAccessPolicyParameter.clearRanges();
        }

        qbdsProcCategoryAccessPolicyParameter.addRange(fieldIdAccessPolicyRule).value(categoryRules);
    }
    else
    {
        qbdsProcCategoryAccessPolicyParameter = this.query().dataSourceTable(tableIdParameter);

        if (qbdsProcCategoryAccessPolicyParameter)
        {
            qbdsProcCategoryAccessPolicyParameter.enabled(false);
        }
    }
    usedAccessPolicyDataTableId = tableIdParameter;
}

public void executeQuery()
{
    this.addCatalogPolicyRule();
    this.addCategoryAccesssPolicyRule();

    EcoResCategoryDescendents_ds.query().dataSourceTable(tableNum(EcoResCategory)).clearRanges();
    EcoResCategoryDescendents_ds.query().dataSourceTable(tableNum(EcoResCategory)).addRange(fieldNum(EcoResCategory, NestedSetLeft)).value(queryRange(ecoResCategory.NestedSetLeft, ecoResCategory.NestedSetRight));
    EcoResCategoryDescendents_ds.query().dataSourceTable(tableNum(EcoResCategory)).addRange(fieldNum(EcoResCategory, CategoryHierarchy)).value(queryValue(ecoResCategory.CategoryHierarchy));
    EcoResCategoryDescendents_ds.query().dataSourceTable(tableNum(EcoResCategory)).addRange(fieldNum(EcoResCategory, IsActive)).value(queryValue(NoYes::Yes));

    super();
}

[SysClientCacheDataMethodAttribute(false)]
public display str productPriceWithCurrency(InventTable _inventTable)
{
    changecompany(dataArea.text())
    {
        return strfmt('%1 %2', _inventTable.purchPcsPrice(), element.getCurrencyCode());
    }
}

public void executeQuery()
{
    QueryBuildDataSource qbds = this.queryBuildDataSource();
    qbds.clearDynalinks();
    qbds.clearRanges();
    qbds.clearLinks();

    qbds.addLink(fieldNum(PurchReqLine, InventDimId), fieldNum(InventDim, inventDimId));
    qbds.addLink(fieldNum(PurchReqLine, InventDimIdDataArea), fieldNum(InventDim, DataAreaId));
    super();
}

