        public void init()
        {
            super();

            Query query = this.query();

            if (query)
            {
                QueryBuildDataSource qbds = query.dataSourceTable(tablenum(EcoResProduct));

                if (qbds)
                {
					qbds.addRange(fieldNum(EcoResProduct, PdsCWProduct)).value(SysQuery::value(NoYes::No));
                }
            }
        }

public void init()
{
    super();

    sourceDocumentLine.setTmp();
}

public void init()
{
    super();
    EcoResProductTranslation::queryAddDefaultLanguage(this.query());
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.setProcurementCategoryHierarchy();

    catalogItem_ds.query().clearCompanyRange();
    catalogitem_ds.query().addCompanyRange(this.text());

    catalogItem_ds.executeQuery();

    nonCatalogItem.BuyingLegalEntity = CompanyInfo::findDataArea(this.text()).RecId;

    element.setDefaultCompany(this.text());

    element.initCurrencyCode();

    return ret;
}

public boolean validate()
{
    boolean ret;
    RefRecId    buyingLegalEntity;

    ret = super();

    buyingLegalEntity = CompanyInfo::findDataArea(this.text()).RecId;
    if (!this.text())
    {
        ret = checkFailed("@SYS311071");
    }

    if (ret && !buyingLegalEntity)
    {
        ret = checkFailed("@SYS311070");
    }

    if (ret)
    {
        ret = purchReqInternalOrgManager_LegalEntity.validate(nonCatalogItem.Requisitioner, buyingLegalEntity);
    }

    if (ret && !element.isConsumption() && !RequisitionPurposeRule::canBeReplenishmentRequisition(buyingLegalEntity))
    {
        ret = checkFailed(strFmt("@SYS4050046",this.text()));
    }

    return ret;
}

/// <summary>
/// Show lookup based on the requisitioner
/// </summary>
public void lookup()
{
    purchReqInternalOrgManager_LegalEntity.lookup(nonCatalogItem.Requisitioner, this);
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        element.categoryNameFilterModified(this.text());
    }

    return ret;
}

