public void dialogClosed(xFormRun _formRun)
{
    super(_formRun);

    if (_formRun.closedOk())
    {
        PurchReqAddNonCatalogIForm purchReqAddNonCatalogForm = _formRun as PurchReqAddNonCatalogIForm;
        element.addNonCatalogItem(purchReqAddNonCatalogForm.getResult());
    }
}

void clicked()
{
    element.setDefaultCompany(dataArea.text());

    element.addCatalogItem();

    super();
}

void clicked()
{
    element.removeLineFromItemBasket();
    super();
}

void clicked()
{
    FormRun formRun;
    Args    args    = new Args();

    args.record(nonCatalogItem);
    args.caller(purchReqAddItemForm);

    formRun = new MenuFunction(this.menuItemName(), this.menuItemType()).create(args);

    if (formRun)
    {
        formRun.run();
        formRun.wait(true);
    }

    purchReqAddItemForm.saveVendorProposalSuggestion(itemBasket.RecId);
}

