public void init()
{
    super();

    this.setButtonAccess();
    this.toggleTabs();
    this.showRequisitioner();
}

void showRequisitioner()
{
    if (purchReqOriginationAuthorizationView.selectionAsPurchReqOriginationAuthorizationView() == PurchReqOriginationAuthorizationView::ByRequisitioner)
    {
        purchReqAuthorizationOrigination_Originator.visible(true);
        Originator_Name.visible(true);

        purchReqAuthorizationOrigination_Requisitioner.visible(false);
        Requisitioner_Name.visible(false);
    }
    else
    {
        purchReqAuthorizationOrigination_Originator.visible(false);
        Originator_Name.visible(false);

        purchReqAuthorizationOrigination_Requisitioner.visible(true);
        Requisitioner_Name.visible(true);
    }
}

public void toggleTabs()
{
    if (purchReqOriginationAuthorizationView.selectionAsPurchReqOriginationAuthorizationView() == PurchReqOriginationAuthorizationView::ByRequisitioner)
    {
        tabAuthorizationOrigination.caption("@SYS127775");
        tabPageLegalEntity.visible(true);
        tabPageReceivingOperatingUnit.visible(true);
    }
    else
    {
        tabAuthorizationOrigination.caption("@SYS304467");
        tabPageLegalEntity.visible(false);
        tabPageReceivingOperatingUnit.visible(false);
    }
}

public int active()
{
    int ret = super();

    purchReqAuthorizationOrigination_ds.executeQuery();

    return ret;
}

public void init()
{
    super();

    QueryBuildDataSource qbds = this.query().dataSourceName('DirPersonName');

    qbds.clearDynalinks();

    qbds.addLink(fieldNum(HcmWorker, Person), fieldNum(DirPersonName, Person));
}

public int active()
{
    int ret = super();

    if (purchReqAuthorizationOrigination.ValidTo < DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()))
    {
        purchReqAuthorizationOrigination_ds.allowEdit(false);
    }
    else
    {
        purchReqAuthorizationOrigination_ds.allowEdit(true);
    }

    return ret;
}

public void initValue()
{
    super();

    //user can create grant permissions in EP, if the permissions are granted in Win then it is administrator
    purchReqAuthorizationOrigination.IsAdminCreated = true;
}

public void write()
{
    super();
    // Send a notification to the new Preparer
    EventNotification_PurchReq::sendPrepareAuthorizationNotification(purchReqAuthorizationOrigination.Originator,
                                                                     purchReqAuthorizationOrigination.Requisitioner);

    proHcmWorker_ds.setLink();

    this.executeQuery();
}

public void executeQuery()
{
    QueryBuildDataSource qbds = this.query().dataSourceName('PurchReqAuthorizationOrigination');
    date validFrom = dateNull();
    date validTo = dateMax();

    this.query().validTimeStateDateRange(validFrom, validTo);

    qbds.clearRange(fieldNum(PurchReqAuthorizationOrigination, Requisitioner));
    qbds.clearRange(fieldNum(PurchReqAuthorizationOrigination, Originator));

    if (purchReqOriginationAuthorizationView.selectionAsPurchReqOriginationAuthorizationView() == PurchReqOriginationAuthorizationView::ByRequisitioner)
    {
        qbds.addRange(fieldNum(PurchReqAuthorizationOrigination, Requisitioner)).value(queryValue(hcmWorker.RecId));
    }
    else
    {
        qbds.addRange(fieldNum(PurchReqAuthorizationOrigination, Originator)).value(queryValue(hcmWorker.RecId));
    }

    super();
}

public void setLink()
{
    QueryBuildDataSource qbds = this.query().dataSourceName('PROHcmWorker');

    qbds.clearDynalinks();

    qbds.clearLinks();

    if (purchReqOriginationAuthorizationView.selectionAsPurchReqOriginationAuthorizationView() == PurchReqOriginationAuthorizationView::ByRequisitioner)
    {
        qbds.addLink(fieldNum(PurchReqAuthorizationOrigination, Originator), fieldNum(HcmWorker, RecId));
    }
    else
    {
        qbds.addLink(fieldNum(PurchReqAuthorizationOrigination, Requisitioner), fieldNum(HcmWorker, RecId));
    }
}

public void executeQuery()
{
    QueryBuildDataSource qbds = this.query().dataSourceName('PurchReqAuthorizationLegalEntity');

    qbds.clearRange(fieldNum(PurchReqAuthorizationLegalEntity, Requisitioner));

    qbds.addRange(fieldNum(PurchReqAuthorizationLegalEntity, Requisitioner)).value(queryValue(hcmWorker.RecId));

    super();
}

public void executeQuery()
{
    QueryBuildDataSource qbds = this.query().dataSourceName('PurchReqAuthorizationOperatingUnit');

    qbds.clearRange(fieldNum(PurchReqAuthorizationOperatingUnit, Requisitioner));

    qbds.addRange(fieldNum(PurchReqAuthorizationOperatingUnit, Requisitioner)).value(queryValue(hcmWorker.RecId));

    super();
}

public boolean modified()
{
    boolean ret = super();

    element.toggleTabs();

    proHcmWorker_ds.setLink();

    purchReqAuthorizationOrigination_ds.executeQuery();

    // Set the appropriate text
    if (purchReqOriginationAuthorizationView.selectionAsPurchReqOriginationAuthorizationView() == PurchReqOriginationAuthorizationView::ByRequisitioner)
    {
        embeddedHelpText.text("@SYS312161");
    }
    else
    {
        embeddedHelpText.text("@SYS305891");
    }

    element.showRequisitioner();

    return ret;
}
