/// <summary>
/// The <c>PurchReqConsolidation</c> form is used to provide interaction with the user with <c>PurchReqConsolidation</c> data and
///  <c>PurchReqConsolidationLine</c> data
/// </summary>
public class PurchReqConsolidation extends FormRun
{
    PurchReqConsolidationForm   purchReqConsolidationForm;
    PurchReqFormMode            purchReqFormMode;
    PurchReqLine                purchReqLines;
    List                        selectedLines;
    TmpFrmVirtual               tmpFrmVirtual;
    SelectableDataArea          startupDataAreaId;
    SelectableDataArea          currentDataAreaId;
}

public void activate(boolean _active)
{
    super(_active);

    if (currentDataAreaId)
    {
        element.setDefaultCompany(currentDataAreaId);
    }
    else
    {
        element.setDefaultCompany(startupDataAreaId);
    }
}

/// <summary>
/// This method applies all of the value changes, if any, made in the <c>PurchReqConsolidationLine</c> records to the corresponding
/// <c>PurchReqLine</c> records.  If no changes were made to a Purchase Requsition Consolidation Line record, the
/// corresponding Purchase Requisition Line record aggregation status is set to 'Consolidation Rejected'
/// </summary>
public void applyOpportunity()
{
    DialogButton        dialogYesNo;
    PurchReqLine        purchReqLine2;
    List                listToUpdate  = new List(Types::Int64);
    List                listToReject  = new List(Types::Int64);
    boolean             noQuoteNumber = false;
    FormDataSource      fds;

    //Traverse through the line items and populate respective Lists
    while select purchReqConsolidationLine where
        purchReqConsolidationLine.PurchReqConsolidationId == purchReqConsolidation.RecId && ! noQuoteNumber
    {
        if (purchReqConsolidationLine.NewVendorQuoteNumber == '')
        {
            noQuoteNumber = true;
        }
        else
        {
            purchReqLine2 = PurchReqLine::find(purchReqConsolidationLine.PurchReqLineID,true);
            if (purchReqLine2)
            {
                //Was a change made?
                if ((purchReqConsolidationLine.NewPurchPrice != purchReqLine2.PurchPrice) ||
                    (purchReqConsolidationLine.NewRequestedDate != purchReqLine2.RequiredDate) ||
                    (purchReqConsolidationLine.NewVendor != purchReqLine2.VendAccount) ||
                    (purchReqConsolidationLine.NewVendorQuoteNumber) ||
                    (purchReqConsolidationLine.NewPurchQty != purchReqLine2.PurchQty) ||
                    (purchReqConsolidationLine.NewLineAmount != purchReqLine2.LineAmount) ||
                    (purchReqConsolidationLine.Discount != purchReqLine2.LineDisc))
                {
                    listToUpdate.addEnd(purchReqConsolidationLine.RecId);
                }
                else
                {
                    listToReject.addEnd(purchReqConsolidationLine.RecId);
                }
            }
        }
    }

    if (! noQuoteNumber)
    {
        if (listToReject.elements() > 0)
        {
            dialogYesNo = Box::yesNo("@SYS130731",DialogButton::No,"@SYS130793");
        }
        if ((listToReject.elements() == 0) || (dialogYesNo))
        {
            PurchReqConsolidationLine::applyOpportunities(listToUpdate, listToReject, purchReqConsolidation);

            if (this.isCalledFromListPage())
            {
                fds = FormDataUtil::getFormDataSource(element.args().record());
                fds.executeQuery();
            }
        }

        purchReqConsolidation_ds.reread();
        purchReqConsolidation_ds.refresh();

        purchReqConsolidationLine_ds.executeQuery();
    }
    else
    {
        error("@SYS136469");
    }
}

public void close()
{
    super();

    if (startupDataAreaId != curext())
    {
        element.setDefaultCompany(startupDataAreaId);
    }
}

private boolean isAnyRequisitionLineInStatus(PurchReqRequisitionStatus _status)
{
    PurchReqLine              reqLine;
    PurchReqConsolidationLine consolidationLine;

    select firstonly RecId, PurchId from reqLine
          join RecId from consolidationLine
            where consolidationLine.PurchReqConsolidationId  == purchReqConsolidation_ds.cursor().RecId
                && consolidationLine.PurchReqLineID          == reqLine.RecId
                && reqLine.RequisitionStatus                 == _status;

    return reqLine.RecId != 0;
}

/// <summary>
/// This method enables and disables buttons
/// </summary>
public void enableButtons()
{
    if (purchReqConsolidation)
    {
        PurchReqConsolidationLine consolidationLine = PurchReqConsolidationLine_DS.cursor() as PurchReqConsolidationLine;
        PurchReqLine              reqLine           = PurchReqLine::find(consolidationLine.PurchReqLineID);

        boolean isConsolidationOpen       = purchReqConsolidation.Status == PurchReqConsolidationActiveStatus::Open;
        boolean isRequisitionLineApproved = reqLine.RequisitionStatus    == PurchReqRequisitionStatus::Approved;
        boolean isRequisitionLineClosed   = reqLine.RequisitionStatus    == PurchReqRequisitionStatus::Closed;
        boolean consolidationHasLines     = consolidationLine.RecId      != 0;

        // Header buttons
        applyOpportunity.enabled(isConsolidationOpen && consolidationHasLines);
        removeConsolidation.enabled(!this.isAnyRequisitionLineInStatus(PurchReqRequisitionStatus::Closed));
        purchaseOrderMenuItemButton.enabled(!isConsolidationOpen && consolidationHasLines && this.isAnyRequisitionLineInStatus(PurchReqRequisitionStatus::Approved));

        // Line buttons
        addOpportunity.enabled(isConsolidationOpen);
        bulkEditSelectedLines.enabled(isConsolidationOpen);
        RemoveConsolidationLine.enabled(!isRequisitionLineClosed);
        createPurchaseOrderFromLine.enabled(!isConsolidationOpen && isRequisitionLineApproved);

        purchReqConsolidation_ds.allowEdit(isConsolidationOpen);
        purchReqConsolidationLine_ds.allowEdit(isConsolidationOpen);
    }
}

/// <summary>
/// Keep track of current company the form was open with.
/// </summary>
/// </exception>
public void init()
{
    startupDataAreaId = curext();
    super();

    this.enableButtons();
}

/// <summary>
/// This method checks the passed args() and validates that the caller is from <c>PurchReqCreatePurchListPage</c>
/// </summary>
/// <returns>
/// True if calling form is <c>PurchReqCreatePurchListPage</c>; otherwise False.
/// </returns>
private boolean isCalledFromListPage()
{
    Object      myCaller;
    FormRun     callerForm;
    boolean     calledFromListPage = false;

    if (element.args())
    {
        myCaller = element.args().caller();
        if (myCaller && myCaller is FormRun)
        {
            callerForm = myCaller;
            if (callerForm.name() == formStr(PurchReqCreatePurchListPage))
            {
                calledFromListPage = true;
            }
        }
    }

    return calledFromListPage;
}

/// <summary>
/// This method updates the <c>PurchReqLine</c> record Consolidation Status as Aggregation Rejected for each selected
/// <c>PurchReqConsolidationLine</c> record.
/// </summary>
public void rejectOpportunity()
{
    DialogButton                dialogYesNo;
    List                        list                                = new List(Types::Int64);
    PurchReqConsolidationLine   purchReqConsolidationLineLocal;
    FormDataSource              fds;

    dialogYesNo = Box::yesNo("@SYS130794",DialogButton::No,"@SYS130793");

    if (dialogYesNo)
    {
        for (purchReqConsolidationLineLocal = purchReqConsolidationLine_DS.getFirst(true) ?
                purchReqConsolidationLine_DS.getFirst(true) : purchReqConsolidationLine_DS.cursor();
        purchReqConsolidationLineLocal; purchReqConsolidationLineLocal = purchReqConsolidationLine_DS.getNext())
        {
            list.addEnd(purchReqConsolidationLineLocal.RecId);
        }

        PurchReqConsolidationLine::rejectOpportunities(list);

        if (element.isCalledFromListPage())
        {
            fds = FormDataUtil::getFormDataSource(element.args().record());
            fds.executeQuery();
        }
    }
}

public void setDefaultCompany(SelectableDataArea _dataAreaId)
{
    if (_dataAreaId != curext())
    {
        appl.setDefaultCompany(_dataAreaId, false);

        currentDataAreaId = _dataAreaId;
    }
}

/// <summary>
/// This method is called whenever a change is made to <c>PurchReqConsolidationLine</c> values.  It provides an approximate
/// total of all the line items based on a single currency code.  It performs the exchange rate on the fly.
/// This method also updates the Consolidation Opportunity total quantity and net amount in the <c>PurchReqConsolidation</c> table.
/// </summary>
public void updateTotals()
{
    PurchReqConsolidationForm::updateTotal(purchReqConsolidation, purchReqConsolidationLine_ds);
    purchReqConsolidation_ds.reread();
    purchReqConsolidation_ds.refresh();

    purchReqConsolidationLine_ds.refresh();
}

/// <summary>
/// Enables refresh from create consolidation form.
/// </summary>
public void refreshDatasource()
{
    purchReqConsolidation_ds.executeQuery();
    purchReqConsolidationLine_ds.executeQuery();
    this.enableButtons();
}

private void viewDetailsJumpRef(str _menuItem, Common _record)
{
    MenuFunction    menuFunction    = new MenuFunction(_menuItem, MenuItemType::Display);
    Args            args            = new Args();

    args.lookupRecord(_record);
    args.caller(element);

    menuFunction.copyCallerQuery(CopyCallerQuery::No);
    menuFunction.run(args);
}

