public void dialogClosed(xFormRun _formRun)
{
    super(_formRun);

    if (_formRun.closedOk())
    {
        element.viewEditModeHelper().setViewEditMode(ViewEditMode::Edit);
    }
}

void clicked()
{
    DialogButton                dialogYesNo;
    List                        list = new List(Types::Int64);
    PurchReqConsolidation       consolidation;
    PurchReqConsolidationLine   consolidationLine;

    super();

    dialogYesNo = Box::yesNo("@SYS312080", DialogButton::No, "@SYS312081");

    if (dialogYesNo)
    {
        consolidation = PurchReqConsolidation::find(purchReqConsolidation_ds.cursor().RecId, true);

        while select RecId, PurchReqLineID from consolidationLine
               where consolidationLine.PurchReqConsolidationId == consolidation.RecId
        {
            list.addEnd(consolidationLine.RecId);
        }

        PurchReqConsolidationLine::rejectOpportunities(list);

        ttsbegin;
        consolidation.delete();
        ttscommit;

        purchReqConsolidationLine_ds.executeQuery();
        purchReqConsolidation_ds.executeQuery();
    }

    element.enableButtons();
}

public void clicked()
{
    /* This is a patch to avoid the problem of the releasing framework processing canceled lines. Instead of fixing it there,
    which is risky and prone to errors, we are stopping the sending of canceled lines from here. There are other scenarios
    where this could happen but they are very unlikely, depending on the UI not being refreshed, so this fix should be enough.
    */
    if (element.isAnyRequisitionLineInStatus(PurchReqRequisitionStatus::Cancelled))
    {
        throw error("@SCM:ConsolidationHasCancelledLinesError");
    }

    super();
    element.enableButtons();
}

void clicked()
{
    super();
    element.applyOpportunity();
}

public boolean modified()
{
    boolean ret;

    ret = super();

    element.updateTotals();

    return ret;
}

void clicked()
{
    Args            args;
    FormRun         formRun;
    int             cnt;

    super();

    cnt = purchReqConsolidation_ds.getPosition();

    try
    {
        args = new Args(formStr(PurchReqConsolidationAddLine));
        args.caller(element);
        args.record(purchReqConsolidation_ds.cursor());
        args.parmEnumType(enumNum(PurchReqFormMode));
        args.parmEnum(PurchReqFormMode::ShowAll);

        formRun = classfactory.formRunClass(args);
        formRun.init();
        formRun.run();
        formRun.wait(true);
    }
    catch(Exception::Break)
    {
        error('Error');
    }

    if (formRun.closedOk())
    {
        purchReqConsolidation_ds.executeQuery();
        purchReqConsolidation_ds.setPosition(cnt);

        purchReqConsolidationLine_ds.executeQuery();

        element.viewEditModeHelper().setViewEditMode(ViewEditMode::Edit);
    }
}

void clicked()
{
    Args                        args;
    FormRun                     formRun;
    List                        list = new List(Types::Int64);
    PurchReqConsolidationLine   purchReqConsolidationLineLocal;

    super();

    for (purchReqConsolidationLineLocal = purchReqConsolidationLine_ds.getFirst(true) ?
            purchReqConsolidationLine_ds.getFirst(true) : purchReqConsolidationLine_ds.cursor();
    purchReqConsolidationLineLocal; purchReqConsolidationLineLocal = purchReqConsolidationLine_ds.getNext())
    {
        list.addEnd(purchReqConsolidationLineLocal.RecId);
    }

    try
    {
        args = new Args(formStr(PurchReqConsolidationBulkEdit));
        args.caller(element);
        args.parmEnumType(enumNum(PurchReqFormMode));
        args.parmEnum(PurchReqFormMode::ShowAll);
        args.parmObject(list);

        formRun = classfactory.formRunClass(args);
        formRun.init();
        formRun.run();
        formRun.wait(true);
    }
    catch(Exception::Break)
    {
        error('Error');
    }

    purchReqConsolidationLine_ds.research(true);
    element.updateTotals();
}

