public class PurchReqControlRFQRule extends FormRun
{
    RefRecID                purchReqControlRFQRuleId;
    RefRecID                policyRuleTypeIndex;
    RefRecID                policyIndex;
    SysPolicyRule           sysPolicyRule;
    boolean                 isNew;
    boolean                 existingInheritedValue;
    EcoResCategoryHierarchy procurementCategoryHierarchy;
}

/// <summary>
///     Determine if buttons on the form should be enable or disable.
/// </summary>
public void enableButtons()
{
    boolean     hasInformalCurrency;
    boolean     hasInformalThreshold;
    boolean     hasFormalCurrency;
    boolean     hasFormalThreshold;

    hasInformalCurrency  = informalThresholdCurrencyCode.valueStr() != '';
    hasInformalThreshold = purchReqControlRFQInformalCatCondition_ds.numberOfRowsLoaded();
    hasFormalCurrency    = formalThresholdCurrencyCode.valueStr() != '';
    hasFormalThreshold   = purchReqControlRFQFormalCatCondition_ds.numberOfRowsLoaded();

    informalThresholdAdd.enabled(hasInformalCurrency);
    informalThresholdDelete.enabled(hasInformalThreshold);
    formalThresholdAdd.enabled(hasFormalCurrency);
    formalThresholdDelete.enabled(hasFormalThreshold);
}

public void init()
{
    QueryBuildRange qbr;
    QueryBuildRange qbr2;

    super();

    if (!element.args().caller())
    {
        throw error(Error::missingFormActiveBuffer(element.name()));
    }

    policyRuleTypeIndex = str2int64(element.args().parm());
    sysPolicyRule       = element.args().record();
    policyIndex         = element.args().caller().getPolicyIndex();

    if (sysPolicyRule)
    {
        SysPolicyUtil::filterPolicyRule(purchReqControlRFQRule_ds, sysPolicyRule);
    }

    //Clear range for PurchReqControlRFQRule
    purchReqControlRFQFormalCatCondition_ds.query().dataSourceTable(tablenum(PurchReqControlRFQCategoryCondition)).clearRange(
                                        fieldnum(PurchReqControlRFQCategoryCondition, PurchReqControlRFQRule));

    //Clear range for IsFormal
    purchReqControlRFQFormalCatCondition_ds.query().dataSourceTable(tablenum(PurchReqControlRFQCategoryCondition)).clearRange(
                                        fieldnum(PurchReqControlRFQCategoryCondition, IsFormal));

    //find/add range for PurchReqControlRFQRule
    qbr = purchReqControlRFQFormalCatCondition_ds.query().dataSourceTable(tablenum(PurchReqControlRFQCategoryCondition))
        .findRange(fieldnum(PurchReqControlRFQCategoryCondition, PurchReqControlRFQRule));
    if ( qbr == null )
    {
        qbr = purchReqControlRFQFormalCatCondition_ds.query().dataSourceTable(tablenum(PurchReqControlRFQCategoryCondition))
            .addRange(fieldnum(PurchReqControlRFQCategoryCondition, PurchReqControlRFQRule));
    }
    qbr.value(queryValue(sysPolicyRule.RecId));

    //find/add range for IsFormal
    qbr2 = purchReqControlRFQFormalCatCondition_ds.query().dataSourceTable(tablenum(PurchReqControlRFQCategoryCondition))
            .findRange(fieldnum(PurchReqControlRFQCategoryCondition, IsFormal));
    if ( qbr2 == null )
    {
        qbr2 = purchReqControlRFQFormalCatCondition_ds.query().dataSourceTable(tablenum(PurchReqControlRFQCategoryCondition))
            .addRange(fieldnum(PurchReqControlRFQCategoryCondition, IsFormal));
    }

    qbr2.value(queryValue(NoYes::Yes));

    //Execute the query
    purchReqControlRFQFormalCatCondition_ds.executeQuery();

    //Clear range for PurchReqControlRFQRule
    purchReqControlRFQFormalCatCondition_ds.query().dataSourceTable(tablenum(PurchReqControlRFQCategoryCondition)).clearRange(
                                        fieldnum(PurchReqControlRFQCategoryCondition, PurchReqControlRFQRule));

    //Clear range for IsFormal
    purchReqControlRFQInformalCatCondition_ds.query().dataSourceTable(tablenum(PurchReqControlRFQCategoryCondition)).clearRange(
                                        fieldnum(PurchReqControlRFQCategoryCondition, IsFormal));

    qbr = purchReqControlRFQInformalCatCondition_ds.query().dataSourceTable(tablenum(PurchReqControlRFQCategoryCondition))
        .findRange(fieldnum(PurchReqControlRFQCategoryCondition, PurchReqControlRFQRule));
    if ( qbr == null )
    {
        qbr = purchReqControlRFQInformalCatCondition_ds.query().dataSourceTable(tablenum(PurchReqControlRFQCategoryCondition))
            .addRange(fieldnum(PurchReqControlRFQCategoryCondition, PurchReqControlRFQRule));
    }
    qbr.value(queryValue(sysPolicyRule.RecId));

    //find/add range for IsFormal
    qbr2 = purchReqControlRFQInformalCatCondition_ds.query().dataSourceTable(tablenum(PurchReqControlRFQCategoryCondition))
            .findRange(fieldnum(PurchReqControlRFQCategoryCondition, IsFormal));
    if ( qbr2 == null )
    {
        qbr2 = purchReqControlRFQInformalCatCondition_ds.query().dataSourceTable(tablenum(PurchReqControlRFQCategoryCondition))
            .addRange(fieldnum(PurchReqControlRFQCategoryCondition, IsFormal));
    }
    qbr2.value(queryValue(NoYes::No));

    purchReqControlRFQInformalCatCondition_ds.executeQuery();

    element.setThresholdGroup();

    if (
        (purchReqControlRFQRule)
        && (purchReqControlRFQRule.ValidTo < DateTimeUtil::utcNow()
            ||
            purchReqControlRFQRule.ValidFrom == purchReqControlRFQRule.ValidTo)
        )
    {
        HeaderGroup.enabled(NoYes::No);
    }
    else
    {
        HeaderGroup.enabled(NoYes::Yes);
    }

    procurementCategoryHierarchy = EcoResCategoryHierarchy::find(EcoResCategoryHierarchyRole::getHierarchiesByRole(EcoResCategoryNamedHierarchyRole::Procurement).CategoryHierarchy);

    element.enableButtons();
}

public void run()
{
    super();
    if (!sysPolicyRule)
    {
        purchReqControlRFQRule_ds.allowCreate(true);
        purchReqControlRFQRule_ds.create();
        purchReqControlRFQRule_ds.initValue();
    }
}

private void setThresholdGroup()
{
    switch (purchReqControlRFQRule.InformalRequirement)
    {
        case PurchReqControlRFQInformalRequirement::Always:
            informalThresholdGroup.visible(false);
            InformalCategoriesThresholdGroupTabPage.visible(false);
            break;

        case PurchReqControlRFQInformalRequirement::Never:
            informalThresholdGroup.visible(false);
            InformalCategoriesThresholdGroupTabPage.visible(false);
            break;

        case PurchReqControlRFQInformalRequirement::Conditional:
            informalThresholdGroup.visible(true);
            InformalCategoriesThresholdGroupTabPage.visible(true);
            break;
    }

    switch (purchReqControlRFQRule.FormalRequirement)
    {
        case PurchReqControlRFQFormalRequirement::Always:
            formalThresholdGroup.visible(false);
            FormalCategoriesThresholdGroupTabPage.visible(false);
            break;

        case PurchReqControlRFQFormalRequirement::Never:
            formalThresholdGroup.visible(false);
            FormalCategoriesThresholdGroupTabPage.visible(false);
            break;

        case PurchReqControlRFQFormalRequirement::Conditional:
            formalThresholdGroup.visible(true);
            FormalCategoriesThresholdGroupTabPage.visible(true);
            break;
    }
}

