[Form]
public class PurchReqSourcingPolicyRule extends FormRun
{
    RefRecId    policyRuleId;
    RefRecId    purchReqSourcingHoldRuleId;
    RefRecId    purchReqConsolidationRuleId;
    boolean     formOpenedForNew;
    DataAreaId  startupDataAreaId;
    DataAreaId  currentCompany;

}

public void activate(boolean _active)
{
    super(_active);

    if (currentCompany != curext())
    {
        appl.setDefaultCompany(currentCompany, false);
    }
}

public void checkConflict()
{
    if (manualPOCreation.selection()         == PurchReqSourcingManualType::Never &&
        consolidationOpportunity.selection() != PurchReqSourcingManualType::Never)
    {
        //Allowing demand consolidation will result in manual purchase order creation. Manual purchase order creation settings will be overridden.
        info("@SYS342738");
    }
}

public void close()
{
    super();

    if (startupDataAreaId != curext())
    {
        appl.setDefaultCompany(startupDataAreaId, false);
    }
}

/// <summary>
///     Determine if buttons on the form should be enable or disable.
/// </summary>
public void enableButtons()
{
    PurchReqSourcingHoldByCategoryRule      internalCatalogCategory;
    PurchReqSourcingHoldByCategoryRule      externalCatalogCategory;
    PurchReqSourcingHoldByCategoryRule      nonCatalogCategory;
    PurchReqSourcingHoldByVendorRule        internalCatalogVendor;
    PurchReqSourcingHoldByVendorRule        externalCatalogVendor;
    PurchReqSourcingHoldByVendorRule        nonCatalogVendor;
    PurchReqConsolidationHoldByCategoryRule internalConsolidationCategory;
    PurchReqConsolidationHoldByCategoryRule externalConsolidationCategory;
    PurchReqConsolidationHoldByCategoryRule nonCatalogConsolidationCategory;
    PurchReqConsolidationHoldByVendorRule   internalConsolidationVendor;
    PurchReqConsolidationHoldByVendorRule   externalConsolidationVendor;
    PurchReqConsolidationHoldByVendorRule   nonCatalogConsolidationVendor;
    boolean                                 canEditDataSource;

    if (formOpenedForNew)
    {
        purchReqSourcingPolicyRule_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    }

    internalCatalogCategory = PurchReqSourcingHoldByCategoryRule::findByHoldRule(purchReqSourcingHoldRuleId,
                                                                                 PurchReqLineType::Item,
                                                                                 false);

    externalCatalogCategory = PurchReqSourcingHoldByCategoryRule::findByHoldRule(purchReqSourcingHoldRuleId,
                                                                                 PurchReqLineType::External,
                                                                                 false);

    nonCatalogCategory = PurchReqSourcingHoldByCategoryRule::findByHoldRule(purchReqSourcingHoldRuleId,
                                                                            PurchReqLineType::Category,
                                                                            false);

    internalCatalogVendor = PurchReqSourcingHoldByVendorRule::findByHoldRule(purchReqSourcingHoldRuleId,
                                                                             PurchReqLineType::Item,
                                                                             false);

    externalCatalogVendor = PurchReqSourcingHoldByVendorRule::findByHoldRule(purchReqSourcingHoldRuleId,
                                                                             PurchReqLineType::External,
                                                                             false);

    nonCatalogVendor = PurchReqSourcingHoldByVendorRule::findByHoldRule(purchReqSourcingHoldRuleId,
                                                                        PurchReqLineType::Category,
                                                                        false);

    internalConsolidationCategory = PurchReqConsolidationHoldByCategoryRule::findByHoldRule(purchReqConsolidationRuleId,
                                                                                            PurchReqLineType::Item,
                                                                                            false);

    externalConsolidationCategory = PurchReqConsolidationHoldByCategoryRule::findByHoldRule(purchReqConsolidationRuleId,
                                                                                            PurchReqLineType::External,
                                                                                            false);

    nonCatalogConsolidationCategory = PurchReqConsolidationHoldByCategoryRule::findByHoldRule(purchReqConsolidationRuleId,
                                                                                              PurchReqLineType::Category,
                                                                                              false);

    internalConsolidationVendor = PurchReqConsolidationHoldByVendorRule::findByHoldRule(purchReqConsolidationRuleId,
                                                                                        PurchReqLineType::Item,
                                                                                        false);

    externalConsolidationVendor = PurchReqConsolidationHoldByVendorRule::findByHoldRule(purchReqConsolidationRuleId,
                                                                                        PurchReqLineType::External,
                                                                                        false);

    nonCatalogConsolidationVendor = PurchReqConsolidationHoldByVendorRule::findByHoldRule(purchReqConsolidationRuleId,
                                                                                          PurchReqLineType::Category,
                                                                                          false);

    // Enable/disable vendor strip add buttons for manual creation
    internalManualVendAdd.enabled(internalCatalogCategory.RecId > 0);
    externalManualVendAdd.enabled(externalCatalogCategory.RecId > 0);
    nonCatManualVendAdd.enabled(nonCatalogCategory.RecId > 0);

    // Enable/disable vendor grid
    internalManualVend.enabled(internalCatalogCategory.RecId > 0);
    externalManualVend.enabled(externalCatalogCategory.RecId > 0);
    nonCatManualVend.enabled(nonCatalogCategory.RecId > 0);

    // Enable/disable vendor strip buttons for consolidation
    internalConVendAdd.enabled(internalConsolidationCategory.RecId > 0);
    externalConVendAdd.enabled(externalConsolidationCategory.RecId > 0);
    nonCatConVendAdd.enabled(nonCatalogConsolidationCategory.RecId > 0);

    // Enable/disable vendor strip buttons for consolidation
    internalConVend.enabled(internalConsolidationCategory.RecId > 0);
    externalConVend.enabled(externalConsolidationCategory.RecId > 0);
    nonCatConVend.enabled(nonCatalogConsolidationCategory.RecId > 0);

    canEditDataSource = purchReqSourcingPolicyRule_ds.canEdit();

    purchReqSourcingSplitRule_ds.allowEdit(canEditDataSource);
    purchReqSourcingPriceToleranceRule_ds.allowEdit(canEditDataSource);
    purchReqSourcingHoldRule_ds.allowEdit(canEditDataSource);
    purchReqSourcingHoldCategoryInternal_ds.allowEdit(canEditDataSource);
    purchReqSourcingHoldVendorInternal_ds.allowEdit(canEditDataSource);
    purchReqSourcingHoldCategoryExternal_ds.allowEdit(canEditDataSource);
    purchReqSourcingHoldCategoryNonCatalog_ds.allowEdit(canEditDataSource);
    purchReqSourcingHoldVendorExternal_ds.allowEdit(canEditDataSource);
    purchReqSourcingHoldVendorNonCatalog_ds.allowEdit(canEditDataSource);
    purchReqConsolidationRule_ds.allowEdit(canEditDataSource);
    purchReqConsolidationCategoryInternal_ds.allowEdit(canEditDataSource);
    purchReqConsolidationCategoryExternal_ds.allowEdit(canEditDataSource);
    purchReqConsolidationCategoryNonCatalog_ds.allowEdit(canEditDataSource);
    purchReqConsolidationVendorInternal_ds.allowEdit(canEditDataSource);
    purchReqConsolidationVendorExternal_ds.allowEdit(canEditDataSource);
    purchReqConsolidationVendorNonCatalog_ds.allowEdit(canEditDataSource);
}

public void init()
{
    RefRecId                                policyIndex;
    RefRecId                                policyRuleTypeIndex;
    PurchReqSourcingPolicyRule              policyRule;
    PurchReqSourcingHoldRule                holdRule;
    PurchReqSourcingHoldRule                holdRuleUpdate;
    PurchReqSourcingHoldByCategoryRule      holdByCategoryRule;
    PurchReqSourcingHoldByVendorRule        holdByVendorRule;
    PurchReqConsolidationRule               consolidateRule;
    PurchReqConsolidationRule               consolidateRuleUpdate;
    PurchReqConsolidationHoldByCategoryRule consolidateByCategoryRule;
    PurchReqConsolidationHoldByVendorRule   consolidateByVendorRule;

    startupDataAreaId = curext();

    currentCompany    = curext();

    if (!element.args().caller())
    {
        throw error(Error::missingFormActiveBuffer(element.name()));
    }

    formOpenedForNew = element.args().parmEnum() == OpenMode::New;

    super();

    policyRule          = element.args().record();
    policyIndex         = element.args().caller().getPolicyIndex();
    policyRuleTypeIndex = str2int64(element.args().parm());

    if (!policyRule)
    {
        policyRule = this.initValues(policyIndex, policyRuleTypeIndex);
    }

    policyRuleId = policyRule.RecId;

    SysPolicyUtil::filterPolicyRule(purchReqSourcingPolicyRule_ds, policyRule);

    holdRule = PurchReqSourcingHoldRule::findByRule(policyRuleId, false);
    purchReqSourcingHoldRuleId = holdRule.RecId;

    if (holdRule.EnableManualHold == PurchReqSourcingManualType::Never
     || holdRule.EnableManualHold == PurchReqSourcingManualType::Always)
    {
        holdByCategoryRule = PurchReqSourcingHoldByCategoryRule::findByHoldRuleId(purchReqSourcingHoldRuleId, false);

        delete_from holdByVendorRule where holdByVendorRule.PurchReqSourcingHoldByCategoryRule == holdByCategoryRule.RecId;
        delete_from holdByCategoryRule where holdByCategoryRule.PurchReqSourcingHoldRule == purchReqSourcingHoldRuleId;

        update_recordset holdRuleUpdate
        setting EnableHoldByPrePayment          = NoYes::No,
                EnableHoldByInternalCatalogItem = PurchReqSourcingCatalogItemHoldType::None,
                EnableHoldByExternalCatalogItem = PurchReqSourcingCatalogItemHoldType::None,
                EnableHoldByNonCatalogItem      = PurchReqSourcingCatalogItemHoldType::None
        where holdRuleUpdate.RecId == holdRule.RecId;
    }

    consolidateRule = PurchReqConsolidationRule::findByRule(policyRuleId, false);
    purchReqConsolidationRuleId = consolidateRule.RecId;

    if (consolidateRule.EnableManualHold == PurchReqSourcingConsolidationType::Never
     || consolidateRule.EnableManualHold == PurchReqSourcingConsolidationType::Always)
    {
        consolidateByCategoryRule = PurchReqConsolidationHoldByCategoryRule::findByHoldRuleId(purchReqConsolidationRuleId, false);

        delete_from consolidateByVendorRule where consolidateByVendorRule.PurchReqConsolidationHoldByCategoryRule == consolidateByCategoryRule.RecId;
        delete_from consolidateByCategoryRule where consolidateByCategoryRule.PurchReqConsolidationRule == purchReqConsolidationRuleId;

        update_recordset consolidateRuleUpdate
        setting EnableHoldByInternalCatalogItem = PurchReqSourcingCatalogItemHoldType::None,
                EnableHoldByExternalCatalogItem = PurchReqSourcingCatalogItemHoldType::None,
                EnableHoldByNonCatalogItem      = PurchReqSourcingCatalogItemHoldType::None
        where consolidateRuleUpdate.RecId == consolidateRule.RecId;
    }

    this.enableButtons();
}

/// <summary>
///     Initialize records for using purchase requisition to purchase order creation rules.
/// </summary>
/// <param name="_policyIndex">
///     The record ID in the <c>SysPolicy</c> table.
/// </param>
/// <param name="_policyRuleTypeIndex">
///     The record ID in the <c>SysPolicyRuleType</c> table.
/// </param>
/// <returns>
///     A record ID in the <c>PurchReqSourcingPolicyRule</c> table.
/// </returns>
public SysPolicyRule initValues(RefRecId _policyIndex, RefRecId _policyRuleTypeIndex)
{
    PurchReqSourcingSplitRule           purchReqSplitRule;
    PurchReqSourcingPriceToleranceRule  purchReqPriceToleranceRule;
    PurchReqSourcingHoldRule            purchReqHoldRule;
    PurchReqConsolidationRule           purchReqConsolidateRule;
    date                                beginDate = dateNull();
    date                                endDate = maxDate();

    purchReqSourcingPolicyRule.initValues(_policyIndex, _policyRuleTypeIndex);
    purchReqSourcingPolicyRule_ds.create();
    purchReqSourcingPolicyRule.data(purchReqSourcingPolicyRule);
    purchReqSourcingPolicyRule_ds.write();

    policyRuleId = purchReqSourcingPolicyRule.RecId;

    if (policyRuleId > 0)
    {
        purchReqSplitRule.initValues(policyRuleId);
        purchReqSourcingSplitRule_ds.create();
        purchReqSourcingSplitRule.data(purchReqSplitRule);
        purchReqSourcingSplitRule_ds.write();

        purchReqPriceToleranceRule.initValues(policyRuleId);
        purchReqSourcingPriceToleranceRule_ds.create();
        purchReqSourcingPriceToleranceRule.data(purchReqPriceToleranceRule);
        purchReqSourcingPriceToleranceRule_ds.write();

        purchReqHoldRule.initValues(policyRuleId);
        purchReqSourcingHoldRule_ds.create();
        purchReqSourcingHoldRule.data(purchReqHoldRule);
        purchReqSourcingHoldRule_ds.write();

        purchReqConsolidateRule.initValues(policyRuleId);
        purchReqConsolidationRule_ds.create();
        purchReqConsolidationRule.data(purchReqConsolidateRule);
        purchReqConsolidationRule_ds.write();
    }

    return purchreqSourcingPolicyRule;
}

