{
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    queryBuildDataSource    = purchReqSourcingSplitRule_q.dataSourceName(identifierStr(PurchReqSourcingSplitRule));
    queryBuildRange         = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(PurchReqSourcingSplitRule, PolicyRule));
    queryBuildRange.value(queryValue(policyRuleId));
    queryBuildRange.status(RangeStatus::Hidden);

    super();
}

