public void enableBatchGenerationCheckbox(boolean _changeValue)
{
    switch (purchReqSourcingHoldRule.EnableManualHold)
    {
        case PurchReqSourcingManualType::Always :
            purchReqSourcingPolicyRule_IsBatchGenerationEnabled.enabled(false);

            if (_changeValue)
            {
                purchReqSourcingPolicyRule.IsBatchGenerationEnabled = false;
                purchReqSourcingPolicyRule_ds.refresh();
            }
            break;
        default :
            purchReqSourcingPolicyRule_IsBatchGenerationEnabled.enabled(true);
    }
}

public void executeQuery()
{
    QueryBuildDataSource queryBuildDataSource;

    queryBuildDataSource = purchReqSourcingHoldRule_ds.query().dataSourceName(identifierStr(PurchReqSourcingHoldRule));
    queryBuildDataSource.addRange(fieldNum(PurchReqSourcingHoldRule, PolicyRule)).value(queryValue(policyRuleId));

    super();
}

public int active()
{
    int ret;

    ret = super();

    // Initialize all catalog item groups
    switch (purchReqSourcingHoldRule.EnableManualHold)
    {
        case PurchReqSourcingManualType::Always:
            manual.visible(false);
            break;

        case PurchReqSourcingManualType::Never:
            manual.visible(false);
            break;

        case PurchReqSourcingManualType::Conditionally:
            manual.visible(true);
            break;
    }

    // Initialize internal catalog item group for manual PO creation
    switch (purchReqSourcingHoldRule.EnableHoldByInternalCatalogItem)
    {
        case PurchReqSourcingCatalogItemHoldType::All:
            internalManualGrid.visible(false);
            break;

        case PurchReqSourcingCatalogItemHoldType::None:
            internalManualGrid.visible(false);
            break;

        case PurchReqSourcingCatalogItemHoldType::Conditionally:
            internalManualGrid.visible(true);
            break;
    }

    // Initialize external catalog item group for manual PO creation
    switch (purchReqSourcingHoldRule.EnableHoldByExternalCatalogItem)
    {
        case PurchReqSourcingCatalogItemHoldType::All:
            externalManualGrid.visible(false);
            break;

        case PurchReqSourcingCatalogItemHoldType::None:
            externalManualGrid.visible(false);
            break;

        case PurchReqSourcingCatalogItemHoldType::Conditionally:
            externalManualGrid.visible(true);
            break;
    }

    // Initialize non-catalog item group for manual PO creation
    switch (purchReqSourcingHoldRule.EnableHoldByNonCatalogItem)
    {
        case PurchReqSourcingCatalogItemHoldType::All:
            nonCatManualGrid.visible(false);
            break;

        case PurchReqSourcingCatalogItemHoldType::None:
            nonCatManualGrid.visible(false);
            break;

        case PurchReqSourcingCatalogItemHoldType::Conditionally:
            nonCatManualGrid.visible(true);
            break;
    }

    purchReqSourcingHoldRule_ds.enableBatchGenerationCheckbox(false);

    return ret;
}

public void modified()
