public void write()
{
    EcoResCategory  ecoResCategory;

    super();

    ecoResCategory = EcoResCategory::find(holdCategoryInternalCategory.value());

    if (!ecoResCategory.isLeaf())
    {
        PurchReqSourcingHoldByCategoryRule::addFromCategory(purchReqSourcingHoldRuleId,
                                                            PurchReqLineType::Item,
                                                            ecoResCategory);

        purchReqSourcingHoldCategoryInternal_ds.executeQuery();
    }

    element.enableButtons();
}

