public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    Common                  ret;
    RefRecId                categoryHierarchyId;
    EcoResCategoryHierarchy categoryHierarchy;

    categoryHierarchyId = EcoResCategoryHierarchyRole::getHierarchiesByRole(EcoResCategoryNamedHierarchyRole::Procurement).CategoryHierarchy;
    categoryHierarchy   = EcoResCategoryHierarchy::find(categoryHierarchyId);
