public int active()
{
    int ret;

    ret = super();

    // Initialize all catalog item groups
    switch (purchReqConsolidationRule.EnableManualHold)
    {
        case PurchReqSourcingConsolidationType::Always:
            consolidation.visible(false);
            break;

        case PurchReqSourcingConsolidationType::Never:
            consolidation.visible(false);
            break;

        case PurchReqSourcingConsolidationType::Conditionally:
            consolidation.visible(true);
            break;
    }

    // Initialize internal catalog item group for demand consolidation
    switch (purchReqConsolidationRule.EnableHoldByInternalCatalogItem)
    {
        case PurchReqSourcingCatalogItemHoldType::All:
            internalConsolidationGrid.visible(false);
            break;

        case PurchReqSourcingCatalogItemHoldType::None:
            internalConsolidationGrid.visible(false);
            break;

        case PurchReqSourcingCatalogItemHoldType::Conditionally:
            internalConsolidationGrid.visible(true);
            break;
    }

    // Initialize external catalog item group for demand consolidation
    switch (purchReqConsolidationRule.EnableHoldByExternalCatalogItem)
    {
        case PurchReqSourcingCatalogItemHoldType::All:
            externalConsolidationGrid.visible(false);
            break;

        case PurchReqSourcingCatalogItemHoldType::None:
            externalConsolidationGrid.visible(false);
            break;

        case PurchReqSourcingCatalogItemHoldType::Conditionally:
            externalConsolidationGrid.visible(true);
            break;
    }

    // Initialize non-catalog item group for demand consolidation
    switch (purchReqConsolidationRule.EnableHoldByNonCatalogItem)
    {
        case PurchReqSourcingCatalogItemHoldType::All:
            nonCatConsolidationGrid.visible(false);
            break;

        case PurchReqSourcingCatalogItemHoldType::None:
            nonCatConsolidationGrid.visible(false);
            break;

        case PurchReqSourcingCatalogItemHoldType::Conditionally:
            nonCatConsolidationGrid.visible(true);
            break;
    }

    return ret;
}

public void executeQuery()
{
    QueryBuildDataSource    queryBuildDataSource;
    QueryBuildRange         queryBuildRange;

    queryBuildDataSource    = purchReqConsolidationCategoryInternal_ds.query().dataSourceName(identifierStr(PurchReqConsolidationCategoryInternal));
    queryBuildRange         = SysQuery::findOrCreateRange(queryBuildDataSource, fieldNum(PurchReqConsolidationHoldByCategoryRule, PurchReqSourcingItemType));
    queryBuildRange.value(queryValue(PurchReqLineType::Item));
    queryBuildRange.status(RangeStatus::Hidden);

    super();
}

public void write()
{
    EcoResCategory  ecoResCategory;

    super();

    ecoResCategory = EcoResCategory::find(categoryInternalCategory.value());

    if (!ecoResCategory.isLeaf())
    {
        PurchReqConsolidationHoldByCategoryRule::addFromCategory(purchReqConsolidationRuleId,
                                                                 PurchReqLineType::Item,
                                                                 ecoResCategory);

        purchReqConsolidationCategoryInternal_ds.executeQuery();
    }

    element.enableButtons();
}

