public boolean allowEditMarkupTrans()
{
    return purchReqLine_ds.allowEdit()
           && purchReqLine.LineType != PurchReqLineType::External
           && (purchReqLine.LineType == PurchReqLineType::Category || !purchReqLine.isInferredDraft());
}

public int task (int _taskID)
{
    #Task
    int ret;
    switch (_taskID)
    {
        case #taskEditRecord:
            DialogButton    button;
            str             dialogMessage;
            FormRun         formRun;
            Args            args;

            if (purchReqTable.canResetToDraftStatus())
            {
                dialogMessage = strFmt("@SYS329116", purchReqTable.PurchReqId, purchReqTable.PurchReqName);
                button = Box::yesNoCancel(dialogMessage, DialogButton::Cancel, "@SYS329115");
                if (button == DialogButton::Yes)
                {
                    PurchReqWFStatusTransitionHelper::setPurchReqTable2Draft(purchReqTable.RecId);
                    element.refreshHeader();
                    element.refreshLines();
                }
                else if (button == DialogButton::Cancel)
                {
                    return ret;
                }
            }

            if (element.args().formViewOption() == FormViewOption::Grid)
            {
                args = new Args();
                args.caller(element);
                args.record(purchReqTable);

                formRun = new MenuFunction(menuitemDisplayStr(PurchReqTable), MenuItemType::Display).create(args);

                formRun.run();
            }
            else
            {
                return super(_taskID);
            }

            break;
        default:
            ret = super(_taskID);
            break;
    }
    return ret;
}

public void close()
{
    xSysLastValue::saveLast(purchReqTableForm);

    super();

    if (startUpDataAreaId != curext())
    {
        appl.setDefaultCompany(startUpDataAreaId, false);
    }
}

