public void init()
{
    workflowIconObservableLink = new FormObservableLink();

    startUpDataAreaId = curext();
    boolean disableBusinessJustificationParentDs = false;
    if (element.args())
    {
        if (element.args().menuItemName() == menuItemDisplayStr(PurchReqLineForEdit)
            && element.args().lookupTable()
            && element.args().lookupTable() == tableNum(PurchReqLine))
        {
            element.args().lookupTable(tableNum(PurchReqTable));
            element.args().lookupField(fieldNum(PurchReqTable, RecId));
            element.args().lookupValue(int642str(PurchReqLine::find(str2int64(this.args().lookupValue())).PurchReqTable));
        }

        if (!element.args().record() && element.args().lookupRecord())
        {
            //called from EP
            element.args().record(element.args().lookupRecord());
            element.args().lookupRecord(null);
        }

        if (element.args().dataset() == tableNum(PurchReqLine))
        {
            callerPurchReqLine  = element.args().record();
            callerPurchReqTable = PurchReqTable::find(callerPurchReqLine.PurchReqTable);
        }
        else if (element.args().dataset() == tableNum(PurchReqTable))
        {
            callerPurchReqTable = element.args().record();
        }

        // If parent form is PurchReqTableListPage and contains PurchReqBusinessJustificationCodes data source,
        // disable the same data source on the current form to avoid circular dependency error
        if (this.isCalledFromListPage())
        {
            FormRun purchReqTableListPage = element.args().caller();
            FormDataSource purchReqTableListPageDs = purchReqTableListPage.dataSource();
            disableBusinessJustificationParentDs =
                purchReqTableListPageDs.query().dataSourceTable(tableNum(PurchReqBusinessJustificationCodes)) != null;
        }
    }

    super();

    if (disableBusinessJustificationParentDs)
    {
        QueryBuildDataSource businessJustificationQuery = PurchReqTable_ds.query().dataSourceTable(tableNum(PurchReqBusinessJustificationCodes));
        if (businessJustificationQuery)
        {
            businessJustificationQuery.enabled(false);
        }
    }

    // Setup the new financial dimension tab for the line
    LineDimensionEntryControl.parmValidateBlockedForManualEntry(true);

    element.updateDesign(InventDimFormDesignUpdate::Init);

    this.instantiatePurchReqTableForm();

    purchReqTableForm.parmPurchReqTableFormRun(element);

    // For Projects: Show only purchase requisitions associated to projects if form is called from the project main menu
    if (purchReqTableForm.project())
    {
        if (!element.args().record()) // called from menu
        {
            purchReqTable_ds.query().dataSourceTable(tableNum(PurchReqTable)).addRange(fieldNum(PurchReqTable, ProjId)).value(SysQuery::valueNotEmptyString());
        }
    }

    purchReqLineRFQHelpTextMap = new Map(Types::Int64, Types::String);

    if (!isConfigurationkeyEnabled(configurationKeyNum(Asset)))
    {
        tabFixedAsset.visible(false);
    }

    purchReqInternalOrgManager_LegalEntity   = new PurchReqInternalOrgManager_LegalEntity();
    purchReqInternalOrgManager_OperatingUnit = new PurchReqInternalOrgManager_OperatingUnit();

    procurementCategoryHierarchy = EcoResCategoryHierarchy::find(EcoResCategoryHierarchyRole::getHierarchiesByRole(EcoResCategoryNamedHierarchyRole::Procurement).CategoryHierarchy);
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));

    element.setVisible_Tax();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        salesPurchOperationTypeController_BR = new SalesPurchOperationTypeController_BR(this.name());

        PurchReqLine_SalesPurchOperationType_BR.visible(true);
        PurchReqLine_CFOPTable_BR.visible(true);
        tabFiscalInformation_BR.visible(true);
    }
    // </GBR>

    // <EPR>
    isRFQCreated = callerPurchReqTable.isRFQCreated();
    // </EPR>

    BudgetReservation_PSN.visible(PublicSectorUtils::isBudgetReservationEnabled());

    element.dataHelper().Refreshing += eventhandler(this.onRefresh);

    if (Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Purchase))
    {
        purchReqLine_ds.object(fieldNum(PurchReqLine, OverrideSalesTax)).visible(true);
    }

    if (callerPurchReqTable)
    {
        PurchReqTableHcmWorkersPolicyHandler::flushPurchReqTableXdsTmp();
    }

    TradeFormsInstrumentationActivities::instrumentTradeFormQueryFilters(formStr(PurchReqTable), purchReqTable_ds.query());

    if (PurchIsSupplierShipmentDatesControlsVisibleFlight::instance().isEnabled())
    {
        Vendor_ShipCalendarId.visible(PurchParameters::find().IsSupplierShipmentDatesEnabled);
    }
}

public void onRefresh(FormRunServiceArgs _cancelEventArgs)
{
    PurchReqTableHcmWorkersPolicyHandler::flushPurchReqTableXdsTmp();
}

/// <summary>
/// Returns the module for instantiating a <c>PurchReqTableForm</c> object.
/// </summary>
/// <returns>The module.</returns>
protected str moduleForInstantiatingPurchReqTableForm()
{
    boolean isProject = (element.args() &&
                              (element.args().dataset() == tableNum(ProjTable)
                            || element.args().dataset() == tableNum(ProjInvoiceTable)
                            || element.args().dataset() == tableNum(ProjWIPTable)));

    if (isProject)
    {
        const str project = 'Project';
        return project;
    }

    return element.args().parm();
}

/// <summary>
///     Instantiates the <c>PurchReqTableForm</c> object.
/// </summary>
protected void instantiatePurchReqTableForm()
{
    if (this.isCalledFromListPage())
    {
        purchReqTableForm = PurchReqTableForm::construct(element.args().record(),
                                                         PurchReqFormMode::Normal,
                                                         this.moduleForInstantiatingPurchReqTableForm());
    }
    else
    {
        purchReqTableForm = PurchReqTableForm::construct(element.args().record(),
                                                         element.args().parmEnum(),
                                                         element.args().parm());
    }
}

protected LogisticsLocationSelectionLookup_Purch createDeliveryPostalAddressLookup(FormReferenceControl _callingControl, InventDim _inventDim)
{
    LogisticsLocationSelectionLookup_Purch lookup = LogisticsLocationSelectionLookup_Purch::newParameters(_callingControl);

    lookup.parmInventSiteId(_inventDim.InventSiteId);
    lookup.parmInventLocationId(_inventDim.InventLocationId);

    lookup.addRoleByType(LogisticsLocationRoleType::Delivery);
    lookup.parmReturnLocation(false);

    return lookup;
}

