private boolean isCalledFromListPage()
{
    Object      myCaller;
    FormRun     callerForm;
    boolean     calledFromListPage = false;
    if (element.args())
    {
        myCaller = element.args().caller();
        if (myCaller && myCaller is FormRun)
        {
            callerForm = myCaller;
            if (callerForm.name() == formStr(PurchReqTableListPage))
            {
                calledFromListPage = true;
            }
        }
    }
    return calledFromListPage;
}

private boolean isPreEncumbranceRequiredForLine()
{
    if (purchReqLine.IsPreEncumbranceRequired == UnknownNoYes::Unknown)
    {
        return PurchReqPreEncumber::isPreEncumbranceEnabled(purchReqLine.BuyingLegalEntity);
    }
    else
    {
        return purchReqLine.IsPreEncumbranceRequired == UnknownNoYes::Yes;
    }
}

private boolean isPurchReqStatusAbleToPerformBudgetCheck()
{
    // Chek if requisition status is InReview, Draft or Rejected
    return  purchReqTable.isInferredDraft() || purchReqTable.RequisitionStatus == PurchReqRequisitionStatus::InReview;
}

