        purchReqTable_ds.reread();
    }
}

public void refreshLines()
{
    purchReqLine_ds.reread();
    purchReqLine_ds.refresh();
}

private void refreshPurchReqSourcingPolicyRuleFlags()
{
    #PurchReqPriceDiscountTransferPolicyRule
    container       purchReqSourcingPolicyRuleContainer;

    // We currently want to query the rule information only when legal entity or receiving operating unit chnaged
    if (purchReqLine.BuyingLegalEntity != buyingLE ||  purchReqLine.ReceivingOperatingUnit != receivingOperatingUnit)
    {
        purchReqSourcingPolicyRuleContainer =
            PurchReqSourcingPolicyRule::getPurchReqSourcingPolicyRuleContainer(purchReqLine.BuyingLegalEntity, purchReqLine.ReceivingOperatingUnit);

        policyPriceDiscountTransferValue = conPeek(purchReqSourcingPolicyRuleContainer, #PosPriceDiscountTransfer);
        allowPriceDiscountTransferOverride = conPeek(purchReqSourcingPolicyRuleContainer, #PosAllowPriceDiscountTransferOverride);

        // Update current legale entity and Receiving operating unit,
        buyingLE = purchReqLine.BuyingLegalEntity;
        receivingOperatingUnit = purchReqLine.ReceivingOperatingUnit;
    }
}

void removeLines()
{
    PurchReqLine   purchReqLineLocal;
    SequenceNumber minSequenceNumber = maxInt();
    boolean        askedConfirmation;

    for (purchReqLineLocal = purchReqLine_ds.getFirst(1) ? purchReqLine_ds.getFirst(1) : purchReqLine_ds.cursor();purchReqLineLocal; purchReqLineLocal = purchReqLine_ds.getNext())
    {
        try
        {
            if (purchReqLine && !askedConfirmation)
            {
                askedConfirmation = true;

                if (Box::okCancel("@SYS333803", DialogButton::Cancel, "@SYS24848") == DialogButton::Cancel)
                {
                    return;
                }
            }

            purchReqLineLocal.delete(false);

            if (purchReqLineLocal.SequenceNumber && minSequenceNumber > purchReqLineLocal.SequenceNumber)
            {
                minSequenceNumber = purchReqLineLocal.SequenceNumber;
            }
        }
        catch (Exception::Error)
        {
            continue;
        }
    }

    if (minSequenceNumber != maxInt())
    {
        purchReqTable.setLineSequence(minSequenceNumber);

        purchReqLine_ds.research();
    }
}

public void rereadLines()
{
    purchReqLine_ds.research(true);
}

public void run()
{
    if (callerPurchReqTable)
    {
        purchReqTable_ds.positionToRecord(callerPurchReqTable);

        if (this.args().openMode() == OpenMode::New)
        {
            this.args().openMode(OpenMode::Edit);
        }
    }

    super();

    allowCreateReqLines = true;
}

