    purchReqTableForm.subscribeToAccDistViewEvents(_accountingDistributionView);
}

void setApprovedOrderedStatusFieldAccess()
{
    purchReqLine_ds.allowEdit(false);
    purchReqLine_ds.allowCreate(false);
}

public void setAssetGroupAccess()
{
    container           assetRules;
    container           optionContainer;
    boolean             qualifierApplicable;
    str                 qualifierQuestion;

    if (isConfigurationkeyEnabled(configurationKeyNum(Asset)) && purchReqLine.ProcurementCategory && purchReqLine.TransDate && purchReqLine.CurrencyCode)
    {
        if (!purchReqLine.ProjId)
        {
            assetRules = purchReqLine.findAndProcessRule();
        }

        if (conLen(assetRules) > 0)
        {
            optionContainer = conPeek(assetRules, OptionContainerPos);
            if (conLen(optionContainer) != 0)
            {
                qualifierApplicable = true;
                qualifierQuestion   = conPeek(assetRules, QualifierTextPos);
            }
        }
    }

    if (qualifierApplicable == true)
    {
        selectedQualifierText.label(qualifierQuestion);
        element.setLineAssetValueAccess(true, true);
    }
    else
    {
        element.setLineAssetValueAccess(false);
    }
}

void setDeliveryAddress(boolean allowEdit = true)
{
    element.design().controlName(DeliveryAddressGroupLine).enabled(allowEdit);
}

void setDistributionTemplateAccess()
{
    if (purchReqLine.isConsumption() && purchReqLine.canApplyAllocationOrDistributionTemplate())
    {
        groupDistributionTemplate.enabled(true);
    }
    else
    {
        groupDistributionTemplate.enabled(false);
    }
}

void setFieldAccess()
{
    boolean isUserTaskOwnerLine = purchReqLine.checkUpdate();

    boolean allowEdit;
    boolean allowEditPriceDiscountTransfer;
    boolean mustDefaultCostPriceAsSalesPrice;
    boolean canEditProjectFields;
    boolean isBudgetReservationReferenced;

    void allowEdit(FieldId _fieldId, boolean _allow = purchReqLine.allowFieldEdit(_fieldId, isUserTaskOwnerLine))
    {
        purchReqLine_ds.object(_fieldId).allowEdit(_allow);
        purchReqLine_ds.object(_fieldId).skip(!_allow);
    }

    allowEdit = purchReqLine.LineType == PurchReqLineType::Category ||
                purchReqLine.isInferredDraft()                      ||
                isUserTaskOwnerLine;

    allowEditPriceDiscountTransfer = isUserTaskOwnerLine && purchReqLine.RequisitionStatus == PurchReqRequisitionStatus::InReview;
    allowEdit(fieldNum(PurchReqLine, LineType));
    allowEdit(fieldNum(PurchReqLine, Name));
    allowEdit(fieldNum(PurchReqLine, PurchQty));
    allowEdit(fieldNum(PurchReqLine, PurchUnitOfMeasure));
    allowEdit(fieldNum(PurchReqLine, PurchPrice));
    allowEdit(fieldNum(PurchReqLine, LineAmount));
    allowEdit(fieldNum(PurchReqLine, CurrencyCode));
    allowEdit(fieldNum(PurchReqLine, PriceUnit));
    allowEdit(fieldNum(PurchReqLine, LinePercent));
    allowEdit(fieldNum(PurchReqLine, LineDisc));
    allowEdit(fieldNum(PurchReqLine, PurchMarkup));
    allowEdit(fieldNum(PurchReqLine, VendAccount));
    allowEdit(fieldNum(PurchReqLine, TaxGroup));
    allowEdit(fieldNum(PurchReqLine, TaxItemGroup));
    if (Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Purchase))
    {
        if (TaxIntegrationPurchReqDocumentPerLineFlight::instance().isEnabled())
        {
            purchReqLine_ds.object(fieldNum(PurchReqLine, OverrideSalesTax)).visible(true);
        }
        allowEdit(fieldNum(PurchReqLine, TaxGroup), PurchReqLine.OverrideSalesTax == true);
        allowEdit(fieldNum(PurchReqLine, TaxItemGroup), PurchReqLine.OverrideSalesTax == true);
    }
    else
    {
        if (TaxIntegrationPurchReqDocumentPerLineFlight::instance().isEnabled())
        {
            purchReqLine_ds.object(fieldNum(PurchReqLine, OverrideSalesTax)).visible(false);
        }
    }
    allowEdit(fieldNum(PurchReqLine, ExternalItemId));

    purchReqLine_ds.object(fieldNum(PurchReqLine, ProcurementCategory)).mandatory(isConsumption);
    purchReqLine_ds.object(fieldNum(PurchReqLine, CurrencyCode)).mandatory(isConsumption);

    itemName.mandatory(false);
    itemName.allowEdit(false);
    item_M_itemName.mandatory(false);
    item_M_itemName.allowEdit(false);

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        allowEdit(fieldNum(PurchReqLine, ProcurementCategory), false);
    }
    else
    {
        // </GBR>
        allowEdit(fieldNum(PurchReqLine, ProcurementCategory), allowEdit && isConsumption);
        // <GBR>
    }
    // </GBR>

    if (purchReqLine.LineType == PurchReqLineType::Category || (!purchReqLine.ItemId && purchReqLine.LineType != PurchReqLineType::External))
    {
        if (purchReqLine.ProcurementCategory)
        {
            if (purchReqLine.RecId)
            {
                purchReqLine_ds.object(fieldNum(PurchReqLine,ProcurementCategory)).allowEdit(false);
            }
            itemName.allowEdit(true);
            itemName.mandatory(true);
            item_M_itemName.allowEdit(true);
            item_M_itemName.mandatory(true);
            allowEdit(fieldNum(PurchReqLine, ItemId), false);
        }
        else
        {
            allowEdit(fieldNum(PurchReqLine, CurrencyCode), false);
        }
    }
    else
    {
        allowEdit(fieldNum(PurchReqLine, ProcurementCategory), false);
        itemName.allowEdit(false);
        item_M_itemName.allowEdit(false);
    }

    if (purchReqLine.LineType == PurchReqLineType::Item || !purchReqLine.ProcurementCategory)
    {
        allowEdit(fieldNum(PurchReqLine, ItemId), allowEdit);
    }
    else
    {
        allowEdit(fieldNum(PurchReqLine, ItemId), false);
    }

    isBudgetReservationReferenced = purchReqLine.isBudgetReservationLineReferenced();

    if (purchReqLine.ProjId && !isBudgetReservationReferenced)
    {
        if (!purchReqLine.ItemId)
        {
            mustDefaultCostPriceAsSalesPrice = ProjCategory::isProjCategoryTypeItem(purchReqLine.ProjCategoryId);
        }

        if (ProjInvoiceTable::isSalesCurrencyLocked(ProjTable::find(purchReqLine.ProjId).ProjInvoiceProjId))
        {
            allowEdit(fieldNum(PurchReqLine, ProjSalesCurrencyId), false);
        }
        else
        {
            allowEdit(fieldNum(PurchReqLine, ProjSalesCurrencyId), !mustDefaultCostPriceAsSalesPrice);
        }

        allowEdit(fieldNum(PurchReqLine, ProjSalesPrice), !mustDefaultCostPriceAsSalesPrice);

        // If there is a project on the purchase requisition line with no budget reservation line referenced then we want the projects fields to be enabled.
        canEditProjectFields = true;

        allowEdit(fieldNum(PurchReqLine, ProjSalesUnitOfMeasure), canEditProjectFields);
    }
    else
    {
        // If the purchase requisition line is referencing the budget reservation line then disable the project related fields.
        // Otherwise keep them enable or disable based on existing logic.

        canEditProjectFields = !isBudgetReservationReferenced && purchReqTable.canEditProjectFieldsOnLine();

        allowEdit(fieldNum(PurchReqLine, ProjId),                   canEditProjectFields);
        allowEdit(fieldNum(PurchReqLine, ActivityNumber),           canEditProjectFields);
        allowEdit(fieldNum(PurchReqLine, ProjCategoryId),           canEditProjectFields);
        allowEdit(fieldNum(PurchReqLine, ProjLinePropertyId),       canEditProjectFields);
        allowEdit(fieldNum(PurchReqLine, ProjSalesCurrencyId),      canEditProjectFields);
        allowEdit(fieldNum(PurchReqLine, ProjSalesPrice),           canEditProjectFields);
        allowEdit(fieldNum(PurchReqLine, ProjSalesUnitOfMeasure),   canEditProjectFields);
        allowEdit(fieldNum(PurchReqLine, ProjTaxGroupId),           canEditProjectFields);
        allowEdit(fieldNum(PurchReqLine, ProjTaxItemGroupId),       canEditProjectFields);
    }

    allowEdit(fieldNum(PurchReqLine, TransDate));
    allowEdit(fieldNum(PurchReqLine, LineComplete));
    allowEdit(fieldNum(PurchReqLine, IsPrepayment));
    allowEdit(fieldNum(PurchReqLine, PrePaymentDetails));
    allowEdit(fieldNum(PurchReqLine, DeliveryName));
    allowEdit(fieldNum(PurchReqLine, DeliveryPostalAddress));
    allowEdit(fieldNum(PurchReqLine, Attention));
    allowEdit(fieldNum(PurchReqLine, AssetGroup));
    allowEdit(fieldNum(PurchReqLine, TaxServiceCode_BR));

    element.setDistributionTemplateAccess();

    allowEdit(fieldNum(PurchReqLine, PriceDiscountTransfer), allowPriceDiscountTransferOverride && allowEditPriceDiscountTransfer );

    groupFinancialDimension.enabled(!isBudgetReservationReferenced);

    if (ProjProjectOperationsIntegrationFeatureHelper::isProjOpsProjectPurchaseOrderFeatureEnabledForCompany())
    {
        ProjectSalesPrice.visible(false);
        ProjectSalesPrice.enabled(false);
    }
}

void setHeaderAccess()
{
    isConsumption           = purchReqTable.isConsumption();
    canRecordPreEncumbrance = isConsumption && purchReqTable.canRecordPreEncumbrance();

    if (purchReqTable.RecId != 0)
    {
        editHeaderAllowed   = purchReqTable.checkUpdate();
        deleteHeaderAllowed = purchReqTable.checkDelete(false);
    }
    else
    {
        editHeaderAllowed = true;
        deleteHeaderAllowed = true;
    }

    purchReqTable_ds.allowEdit(editHeaderAllowed);
    purchReqTable_ds.allowDelete(deleteHeaderAllowed);
    businessJustificationHeader_ds.allowEdit(purchReqTable.BusinessJustification && editHeaderAllowed);
    purchReqTable_ds.object(fieldNum(PurchReqTable, TransDate)).allowEdit(isConsumption);
    purchReqTable_ds.object(fieldNum(PurchReqTable, TransDate)).skip(!isConsumption);

    element.setHeaderButtonAccess();
}

void setHeaderButtonAccess()
{
    boolean enableHistoryControls   = PurchReqTableHistory::existsHistory(purchReqTable.RecId);

    purchCopyAllHeader.enabled(editHeaderAllowed && PurchReqLine::existLine());
    PurchReqTable_DS.allowDelete(purchReqTable.checkDelete(false));

    purchReqCancelHeader.enabled(PurchReqCancel::isCancelButtonEnabled(purchReqTable.TableId, purchReqTable.RecId));
    reserveBudgetFundsButton.enabled(canRecordPreEncumbrance ||
        (purchReqTable.RequisitionStatus == PurchReqRequisitionStatus::Approved && purchReqTable.notPostedLineExists())
        );
    budgetControlHeader.enabled(canRecordPreEncumbrance || this.isPurchReqStatusAbleToPerformBudgetCheck());

    // Enable / disable history buttons
    VersionsButton.enabled(enableHistoryControls);
    CompareButton.enabled(enableHistoryControls);
    RequisitionFinancialsActionPaneTab.enabled(purchReqTable.isConsumption());
    TotalsMenuItemButton.enabled(purchReqTable.isConsumption());
    this.setLineButtonAccess();

    // <EPR>
    element.setOnHoldControlsAccess();
    // </EPR>
}

void setLineAccess()
{
    int64               orgArray[2];
    int64               purchReqControlRuleRecId;
    PurchReqControlRule purchReqControlRule;

    orgArray[1] = purchReqLine.BuyingLegalEntity;
    orgArray[2] = purchReqLine.ReceivingOperatingUnit;

    purchReqControlRuleRecId = SysPolicies::getPolicyRuleID(
                                    orgArray,
                                    SysPolicyRuleType::findByRuleTypePolicyType(
                                            SysPolicyRuleTypeEnum::PurchReqControlRule,
                                            SysPolicyTypeEnum::PurchasingPolicy
                                        ).RecId,
                                    SysPolicyTypeEnum::PurchasingPolicy);

    purchReqControlRule = PurchReqControlRule::find(purchReqControlRuleRecId);

    purchReqLine_ds.object(fieldNum(PurchReqLine, Requisitioner)).allowEdit(purchReqLine.RecId == 0);

    if (purchReqLine.RecId != 0)
    {
        editLineAllowed   = purchReqLine.checkUpdate() && !purchReqLine.isRFQPending();
        deleteLineAllowed = purchReqLine.checkDelete();
    }
    else
    {
        editLineAllowed   = purchReqTable.checkUpdate();
        deleteLineAllowed = purchReqTable.checkUpdate();
    }

    purchReqLine_ds.allowEdit(editLineAllowed);
    purchReqLine_ds.allowCreate(editLineAllowed && purchReqTable.RequisitionStatus == PurchReqRequisitionStatus::Draft);
    purchReqLine_ds.allowDelete(deleteLineAllowed);
    inventDim_ds.allowEdit(editLineAllowed);
    businessJustificationLine_ds.allowEdit(purchReqLine.BusinessJustification && editLineAllowed);

    // Refresh PurchReqSourcingPolicyRule settings if legal entity or receiving operating unit changed
    this.refreshPurchReqSourcingPolicyRuleFlags();

    PriceDiscountTransfer.visible(isConsumption && purchReqLine.LineType == PurchReqLineType::Item);
    PolicyPriceDiscountTransferString.visible(purchReqLine.PriceDiscountTransfer == PurchReqPriceDiscountTransfer::UsePolicy
        && PriceDiscountTransfer.visible());

    element.setFieldAccess();
    buttonCompleteQuestionnaire.enabled(false);

    references_M_editPurchAgreementId.allowEdit(false);

    if (editLineAllowed)
    {
        if (purchReqTable.isInferredDraft())
        {
            addAddress.enabled(purchReqControlRule ? purchReqControlRule.AlternateAddress : true);
        }
        else
        {
            addAddress.enabled(true);
        }

        alternateAddress.enabled(true);
    }
    else
    {
        addAddress.enabled(false);

        alternateAddress.enabled(false);
    }

    if (purchReqTable.isInferredDraft())
    {
        buttonCompleteQuestionnaire.enabled(true);
    }
    else
    {
        if (purchReqLine.purchReqTable().RequisitionStatus == PurchReqRequisitionStatus::InReview)
        {
            element.setPendingCompleteStateFieldAccess();
        }
    }

    if (purchReqLine.RequisitionStatus == PurchReqRequisitionStatus::Approved)
    {
        if (PurchReqLineProcessingError::findByPurchReqLine(purchReqLine.RecId, PurchReqLineProcessingErrorType::PreEncumbranceError))
        {
            if (PurchReqTableForm::isTransDateVisibleForLine(purchReqLine))
            {
                purchReqLine_ds.object(fieldNum(PurchReqLine, TransDate)).allowEdit(true);
            }
            else
            {
                purchReqLine_ds.object(fieldNum(PurchReqLine, RequiredDate)).allowEdit(true);
            }
        }
    }

    if (purchReqLine.RequisitionStatus == PurchReqRequisitionStatus::Closed)
    {
        element.setApprovedOrderedStatusFieldAccess();
    }
    tabFinancialDimensions.allowEdit(purchReqLine.isConsumption());
    tabFinancialDimensions.skip(!purchReqLine.isConsumption());

    element.setLineButtonAccess();
}

public void setLineAssetValueAccess(boolean _forceVisible, boolean _forceEnable = false)
{
    void runCommonCode()
    {
        setToDefaultButton.visible(true);
        selectedQualifierText.allowEdit(_forceEnable
                                        && isConsumption
                                        );
        selectedQualifierText.skip(!(_forceEnable && isConsumption));

        if (purchReqLine.AssetGroup)
        {
            grpAdditionalAssetInformation.allowEdit(
                                        isConsumption
                                        );
            editReasonCode.allowEdit(
                                        isConsumption
                                        );
            grpAdditionalAssetInformation.skip(!isConsumption);
            editReasonCode.skip(!isConsumption);
        }
    }

    purchReqLine_ds.object(fieldNum(PurchReqLine,AssetGroup)).allowEdit(false);

    grpAdditionalAssetInformation.allowEdit(false);
    editReasonCode.allowEdit(false);
    purchReqLine_ds.object(fieldNum(PurchReqLine,AssetGroup)).skip(true);
    grpAdditionalAssetInformation.skip(true);
    editReasonCode.skip(true);

    if (purchReqLine.AssetRuleQualifierOption || purchReqLine.AssetRuleQualifierOptionLocal || _forceVisible)
    {
        //txtAssetQualifier.visible(true);
        selectedQualifierText.visible(true);
        selectedQualifierText.allowEdit(_forceEnable
                                        && isConsumption
                                        );
        selectedQualifierText.skip(!(_forceEnable && isConsumption));
        //window1.visible(true);
    }
    else
    {
        //txtAssetQualifier.visible(false);
        selectedQualifierText.visible(false);
        //txtAssetQualifier.visible(false);
        //window1.visible(false);
    }
    if (purchReqLine.AssetRuleQualifierOption)
    {
        selectedQualifierText.label(AssetRule::getQualifierQuestion(purchReqLine.AssetRuleQualifierOption, true));
    }
    else if (purchReqLine.AssetRuleQualifierOptionLocal)
    {
        selectedQualifierText.label(AssetRule::getQualifierQuestion(purchReqLine.AssetRuleQualifierOptionLocal, false));
    }

    switch (purchReqLine.RequisitionStatus)
    {
        case PurchReqRequisitionStatus::InReview :
            if (isUserTaskOwner)
            {
                runCommonCode();
            }

            selectedQualifierText.allowEdit(_forceEnable
                                        && isConsumption
                                        );
            setToDefaultButton.enabled(
                                        isConsumption
                                        );
            purchReqLine_ds.object(fieldNum(PurchReqLine,AssetGroup)).allowEdit(
                                                                                isConsumption
                                                                                );
            selectedQualifierText.skip(!(_forceEnable && isConsumption));
            setToDefaultButton.skip(!isConsumption);
            purchReqLine_ds.object(fieldNum(PurchReqLine,AssetGroup)).skip(!isConsumption);

            break;

        case PurchReqRequisitionStatus::Draft :
        case PurchReqRequisitionStatus::Rejected:
            runCommonCode();

            setToDefaultButton.enabled(false);
            setToDefaultButton.skip(!true);
            //txtAssetQualifier.visible(_forceVisible);
            selectedQualifierText.visible(_forceVisible);
            //txtAssetQualifier.visible(_forceVisible);
            //window1.visible(_forceVisible);

            break;

        default :
    }
}

void setLineButtonAccess()
{
    boolean existRecord                 = purchReqLine.RecId != 0;
    boolean existConsumptionRecord      = existRecord && isConsumption;
    boolean enablePreEncumbranceButton  = existRecord && element.isPreEncumbranceRequiredForLine();
    boolean enableVendorProposalButton  = editLineAllowed && purchReqLine.isVendorProposalEnabled();
    boolean enableNewAddRemoveButtons   = purchReqLine_ds.allowEdit() && purchReqTable.RequisitionStatus == PurchReqRequisitionStatus::Draft;
    boolean isExternalCatalogItem       = purchReqLine.LineType == PurchReqLineType::External;
    boolean enableBudgetFundsControls   = canRecordPreEncumbrance || this.isPurchReqStatusAbleToPerformBudgetCheck();

    boolean explosionVisible;
    boolean explosionEnabled;

    // Enable / disable add-remove buttons.
    purchReqNewLine.enabled(enableNewAddRemoveButtons);
    purchReqAddItem.enabled(enableNewAddRemoveButtons);
    CatExternalCatalogPunchoutDialog.enabled(enableNewAddRemoveButtons);
    purchReqRemoveItem.enabled(enableNewAddRemoveButtons);

    // Enable / disable buttons based on PR line count
    procCategoryGuideline.enabled(existConsumptionRecord);

    // Enable / disable preview pre-encumbrance
    subLedgerJournalLines.enabled(enablePreEncumbranceButton);

    // Enable / disable accounting distributions buttons
    accountingDistributions.enabled(existRecord);

    // Enable / disable sales tax buttons
    salesTax.enabled(existRecord);

    // Enable / disable purchase order buttons
    vendPurchOrderJournal.enabled(existConsumptionRecord);

    // Enable / disable vendor invoice buttons
    vendInvoiceJournal.enabled(existConsumptionRecord);

    // Enable / disable budget buttons
    budgetCheckResultsLine.enabled(existConsumptionRecord);
    budgetControlLine.enabled(existRecord && enableBudgetFundsControls);

    // Enable / disable committed cost buttons
    lineInquiry_CostControlCommittedCost.enabled(existConsumptionRecord);

    // Enable / disable project budget buttons
    projBudgetDisplay.enabled(existConsumptionRecord);

    requestForQuoteMenuItemButton.enabled(purchReqTable.RequisitionStatus == PurchReqRequisitionStatus::InReview
                                        && isConsumption
                                        && !isExternalCatalogItem
                                        // <EPR>
                                        && !purchReqTable.OnHold
                                        // </EPR>
                                        );

    // Enable / disable vendor proposal buttons
    purchReqLineVendorProposal.enabled(enableVendorProposalButton);

    // toggle misc. charges buttons
    markupTrans_LinePurchReq.visible(!purchReqLine.isInferredDraft() && purchReqLine_ds.allowEdit());
    markupTrans_LinePurchReqView.visible(!markupTrans_LinePurchReq.visible());

    markupTrans_LinePurchReq.enabled(existRecord);
    markupTrans_LinePurchReqView.enabled(existRecord);

    // Enable / disable on-hand buttons
    inventOnhandItem.enabled(existRecord && purchReqLine.forInventory());

    lineGridFinancialsMenuButton.enabled(isConsumption);
    PurchReqLine_DeliveryPostalAddressGrp.enabled(isConsumption);

    requisitionLineReferences.enabled(existRecord && !isConsumption);

    // Enable / disable explosion buttons
    explosionVisible = !isConsumption;
    lineGridExplosion.visible(explosionVisible);

    if (explosionVisible)
    {
        explosionEnabled = purchReqLine.RequisitionStatus == PurchReqRequisitionStatus::Draft
                        || purchReqLine.RequisitionStatus == PurchReqRequisitionStatus::InReview;

        lineGridExplosion.enabled(explosionEnabled);
    }

    lineGridSupplySeparator.visible(explosionVisible);
}

/// <summary>
/// Enable or disable the <c>PurchReqCopyRFQforListPage</c> actionpane button and <c>HoldExplanation</c> field
/// depending on the state of the <c>Hold</c> field.
/// </summary>
public void setOnHoldControlsAccess()
{
    purchReqTable_ds.object(fieldNum(PurchReqTable, OnHold)).allowEdit(!isRFQCreated);
    purchReqTable_ds.object(fieldNum(PurchReqTable, HoldExplanation)).allowEdit(purchReqTable.OnHold);
    RequestForQuoteMenuItemButton.enabled(!purchReqTable.OnHold && !isRFQCreated);
}

void setPendingCompleteStateFieldAccess()
{
    boolean allowEditPurchAgreement = false;

    if (purchReqLine.checkUpdate() &&
        purchReqLine.LineType != PurchReqLineType::External)
    {
        allowEditPurchAgreement = true;
    }

    purchReqLine_ds.object(fieldNum(PurchReqLine, RequiredDate)).allowEdit(true);
    purchReqLine_ds.object(fieldNum(PurchReqLine, TransDate)).allowEdit(true);
    purchReqLine_ds.object(fieldNum(PurchReqLine, BusinessJustification)).allowEdit(true);
    purchReqLine_ds.object(fieldNum(PurchReqLine, AssetGroup)).allowEdit(true);

    purchReqLineKMCollection_ds.allowEdit(true);
    tabFinancialDimensions.allowEdit(true);

    purchReqLine_ds.object(fieldNum(PurchReqLine, IsPrepayment)).allowEdit(purchReqLine.LineType != PurchReqLineType::External);
    references_M_editPurchAgreementId.allowEdit(allowEditPurchAgreement);
    inventDim_ds.allowEdit(purchReqLine.LineType != PurchReqLineType::External);
}

public void setToDefaultAssetGroup()
{
    container con = purchReqLine.initAssetGroup();

    if (purchReqLine)
    {
        purchReqLine.doUpdate();
    }
    if (conPeek(con,1) == true)
    {
        selectedQualifierText.label(conPeek(con,2));
        element.setLineAssetValueAccess(true, true);
    }
    else
    {
        element.setLineAssetValueAccess(false);
    }
}

