void subLedgerJournalLinesClicked()
{
    Args                    args = new Args();
    FormRun                 formRun;
    SourceDocumentLine      sourceDocumentLine;
    SourceDocumentLineList  sourceDocumentLineList = SourceDocumentLineList::construct();

    args.name(formStr(SubledgerJournalAccountEntry));
    args.caller(element);
    args.record(purchReqTable);

    sourceDocumentLine = SourceDocumentLine::find(purchReqLine.SourceDocumentLine);
    sourceDocumentLineList.addEnd(sourceDocumentLine);
    args.parmObject(sourceDocumentLineList);

    formRun = classfactory.formRunClass(args);
    formRun.init();
    formRun.run();
    formRun.wait(true);
}

