{
    if (purchReqLine.isConsumption())
    {
        purchReqLine.setDefaultDimension();
    }
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_PurchReq::newFromForm(element);
            }
            break;

        case InventDimFormDesignUpdate::Active, InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(purchReqLine.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;

        default :
            // Incorrect parameter for the function '%1'
            throw error(strFmt("@SYS54195", funcName()));
    }
}

private void refreshFormPart(str partName)
{
    PartList partList = new PartList(this);

    // For each part in the form
    for (int i = 1; i <= partList.partCount(); i++)
    {
        FormPartControl partControl = partList.getPartControlById(i);

        if (partControl && partControl.formName() == partName)
        {
            partControl.refresh();
            break;
        }
    }
}

