    ExportToExcelDataEntityContext context = null;
    return context;
}

/// <summary>
///  Override the record context if the RequestQueryParameterCollection of current host url has wrong filter
///  for the line level workflow which uses the header table but line level value
/// </summary>
/// <param name = "_recordContext">The current record context.</param>
/// <returns>A boolean value to show the apply record context is good.</returns>
public boolean applyRecordContext(str _recordContext)
{
    boolean ret;

    if (this.args().menuItemName() == menuItemDisplayStr(PurchReqTable) ||
        this.args().menuItemName() == menuItemDisplayStr(PurchReqLineForEdit))
    {
        var generator   = new Microsoft.Dynamics.AX.Framework.Utilities.UrlHelper.UrlGenerator();
        var currentHost = new System.Uri(UrlUtility::getUrl());

        // Parse a current host url to retrieve the RequestQueryParameterCollection
        generator.ParseFullURL(currentHost.AbsoluteUri);

        var requestQueryParameterCollection = generator.RequestQueryParameterCollection;

        if (requestQueryParameterCollection.GetParameterCount() > 0)
        {
            str field, value;
            boolean dataSourceBlank, found;

            while (requestQueryParameterCollection.MoveNext())
            {
                var requestQueryParameter = requestQueryParameterCollection.Current();

                if (requestQueryParameter.DataSource == '')
                {
                    requestQueryParameter.DataSource = tableStr(PurchReqTable);
                    dataSourceBlank = true;
                }

                if (requestQueryParameter.DataSource == tableStr(PurchReqTable) &&
                    requestQueryParameter.GetFieldValueCount() > 0)
                {
                    for (int i = 0; i < requestQueryParameter.GetFieldValueCount(); i++)
                    {
                        field = requestQueryParameter.getField(i);
                        value = requestQueryParameter.getValue(i);

                        // When coming from the line level workflow, the filter can contain a RecId that it thinks is from the PurchReqTable,
                        // but in reality is from the PurchReqLine table and needs to be resolved against its actual header record.
                        if (field == fieldId2Name(tableNum(PurchReqTable), fieldNum(PurchReqTable, RecId)))
                        {
                            // Try to find the PurchReqTable, if not, find the line and change the filter context to the PurchReqTable header
                            if (this.args().menuItemName() == menuItemDisplayStr(PurchReqLineForEdit)
                                || !PurchReqTable::find(str2Int64(value)).RecId)
                            {
                                PurchReqLine purchReqLineLocal = PurchReqLine::find(str2Int64(value));
                                if (purchReqLineLocal.RecId)
                                {
                                    value = int642Str(PurchReqTable::find(purchReqLineLocal.PurchReqTable).RecId);

                                    requestQueryParameter.UpdateEntry(field, value);
                                    found = true;
                                    break;
                                }
                            }
                        }
                    }
                }
                if (found || dataSourceBlank)
                {
                    _recordContext = generator.GenerateRequestQueryString();

                    break;
                }
            }
        }
    }

    ret = super(_recordContext);

    return ret;
}

/// <summary>
/// Initializes the query based on a purchase requisition ID.
/// </summary>
/// <param name = "_purchReqId">The purchase requisition ID.</param>
protected final void initializeQueryWithPurchReqIdRange(PurchReqId _purchReqId)
{
    Query query = new Query();
    QueryBuildRange queryBuildRange = query.addDataSource(tableNum(PurchReqTable)).addRange(fieldNum(PurchReqTable, PurchReqId));
    queryBuildRange.value(queryValue(_purchReqId));
    purchReqTable_ds.query(query);
}

public void init()
{
    CostControlTransCommittedCost   costControlTransCommittedCost;
    PurchRFQCaseLine                purchRFQCaseLine;
    PurchReqLine                    localPurchReqLine;

    super();

    if (element.args().dataset() && element.args().record())
    {
        switch (element.args().dataset())
        {
            case tableNum(CostControlTransCommittedCost):
                costControlTransCommittedCost = element.args().record();
                this.initializeQueryWithPurchReqIdRange(costControlTransCommittedCost.RefId);
                break;

            case tableNum(purchRFQCaseLine):
                purchRFQCaseLine = element.args().record();
                localPurchReqLine = purchRFQCaseLine.purchReqLine();
                this.initializeQueryWithPurchReqIdRange(localPurchReqLine.purchReqId());
                break;
        }
    }

    if (!PurchReqTableForm::isTransDateVisible())
    {
        purchReqTable_ds.object(fieldNum(PurchReqTable, TransDate)).visible(false);
    }
}

public void executeQuery()
{
    XDSServices services;
    str prevXdsContext;
    boolean accessToPurchReqTableAll = PurchReqTableHcmWorkersPolicyHandler::checkCurrentUserAccessToPurchReqTableAll();

    try
    {
        if (!accessToPurchReqTableAll)
        {
            services = new XDSServices();
            prevXdsContext = PurchReqTableHcmWorkersPolicyHandler::getCurrentXdsContextString(services);
            PurchReqTableHcmWorkersPolicyHandler::setCurrentXdsContextString(services);
        }
        super();
        element.setHeaderAccess();
    }
    finally
    {
        if (!accessToPurchReqTableAll)
        {
            PurchReqTableHcmWorkersPolicyHandler::setCurrentXdsContextString(services, prevXdsContext);
        }
    }
}

public void delete()
{
    super();

    element.updateWorkflowButtonEnabled();
    element.updateWorkflowButtonForLineVisibility();

    element.close();
}

void rereadLines()
{
    purchReqLine_ds.executeQuery();
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        purchReqTable2LineUpdatePrompt = new PurchReqTable2LineUpdatePrompt();

        purchReqTable2LineUpdatePrompt.purchReqTable(purchReqTable);

        if (!purchReqTable2LineUpdatePrompt.prompt())
        {
            ret = false;
        }
    }

    return ret;
}

public void write()
{
    ttsbegin;

    super();

    if (purchReqTable2LineUpdatePrompt)
    {
        purchReqTable2LineUpdatePrompt.runOperation();

        purchReqTable2LineUpdatePrompt = null;

        purchReqLine_ds.research(true);
    }

    ttscommit;

    element.updateWorkflowButtonEnabled();
    element.updateWorkflowButtonForLineVisibility();
}

public int active()
{
    int ret;

    ret = super();

    element.setHeaderAccess();

    originatorName.text(purchReqTable.originatorName());

    if (purchReqTable.RequisitionStatus == PurchReqRequisitionStatus::InReview)
    {
        isUserTaskOwner = WorkflowWorkItem::isUserTaskOwner(purchReqTable);
    }
    else
    {
        isUserTaskOwner = false;
    }

    element.updateWorkflowButtonEnabled();
    element.updateWorkflowButtonForLineVisibility();

    if (isNewRecord)
    {
        lineGrid.setFocus();
        isNewRecord = false;
    }
    return ret;
}

