{
    if (!HcmWorkerLookup::currentWorker())
    {
        throw error("@SYS330588"); // To enter a purchase requisition your user ID must be assigned to an active worker. Contact your Microsoft Dynamics Rainier system administrator.
    }
    RequisitionButton.clicked();

    element.updateWorkflowButtonEnabled();
    element.updateWorkflowButtonForLineVisibility();
}

