/// <summary>
/// Display method used to populate value for additional control for Price/discount transfer for line.
/// It displays current policy value.
/// </summary>
/// <returns>
/// Formatted string based on fixed label and current value for policy setting.
/// </returns>
public display String255 PriceDiscountTransferPolicy()
{
    SysDictEnum purchReqPolicyPriceDiscountTransfer = new SysDictEnum(EnumNum(PurchReqPolicyPriceDiscountTransfer));

    return strFmt("@SYS4004687", purchReqPolicyPriceDiscountTransfer.value2label(policyPriceDiscountTransferValue));
}

public void resetLine()
{
    purchReqLine.InventDimIdDataArea = currentDataAreaId;
    inventDim_ds.initValue();
    purchReqLine_ds.cacheCalculateMethod(tableMethodStr(PurchReqLine, editItemName));
    purchReqLine_ds.cacheCalculateMethod(tableMethodStr(PurchReqLine, vendName));
    purchReqLine_ds.rereadReferenceDataSources();
}

