        purchReqLine_ds.positionToRecord(callerPurchReqLine);
    }
}

/// <summary>
/// It validates if CFOP is present in CFOPMatrix with the correct attributes
/// (Operation ID, Transaction Type, Direction and Location)
/// </summary>
/// <returns>
/// It returns true if it is valid.
/// </returns>
public boolean checkCFOP_BR()
{
    boolean ok;

    ok = CFOPValidation_BR::construct(purchReqLine).checkCFOP();

    return ok;
}

public void initInternalOrganizationValues()
{
    RefRecId origBuyingLegalEntity      = purchReqLine.BuyingLegalEntity;

    boolean isCurrentLegalEntityValid   = confind(HcmWorkerHelper::getLegalEntityRecIds(purchReqLine.Requisitioner), CompanyInfo::current());
    purchReqLine.BuyingLegalEntity      = isCurrentLegalEntityValid ? CompanyInfo::current() : HcmWorkerHelper::getLegalEntity(purchReqLine.Requisitioner).RecId;

    if (origBuyingLegalEntity != purchReqLine.BuyingLegalEntity)
    {
        purchReqLine.modifiedField(fieldNum(PurchReqLine, BuyingLegalEntity));
    }

    purchReqLine.ReceivingOperatingUnit = HcmWorkerHelper::getPrimaryDepartmentRecId(purchReqLine.Requisitioner);
    purchReqLine.modifiedField(fieldNum(PurchReqLine, ReceivingOperatingUnit));

    purchReqLine.setDefaultCompany();
    currentDataAreaId = curext();

    this.resetLine();
}

edit Description selectedQualifierText(boolean _set, PurchReqLine _purchReqLine, Description _value)
{
    if (_set)
    {
        if (purchReqLine.ProcurementCategory && purchReqLine.TransDate && purchReqLine.CurrencyCode)
        {
            var assetRules = purchReqLine.assetProcessedRule();
            purchReqLine.setAssetRuleQualifierOptionFromQualifierString(_value, assetRules);
        }
    }
    return purchReqLine.selectedQualifierText();
}

public void delete()
{
    super();

    element.setLineButtonAccess();
    element.updateWorkflowButtonEnabled();
    element.updateWorkflowButtonForLineVisibility();

    element.rereadLines();
}

public int active()
{
    // <GBR>
    boolean             enableCFOPCFPSFields;
    CFOPValidation_BR   cfopValidation;
    // </GBR>

    int ret;

    container assetRules;

    const int qualifierPos = 5;

    purchReqLine.setDefaultCompany();
    currentDataAreaId = curext();

    ret = super();

    if (purchReqLine.BuyingLegalEntity)
    {
        LineDimensionEntryControl.parmCompany(purchReqLine.buyingLegalEntity2DataArea());
        LineDimensionEntryControl.reactivate();
    }

    inventDimFormSetup.parmRequisitionPurpose(purchReqLine.RequisitionPurpose);
    element.updateDesign(InventDimFormDesignUpdate::Active);

    element.setLineAccess();

    tabLineDetails.refreshAsNeeded();

    if (purchReqLine.rfqRequirement != PurchReqRFQRequirement::None)
    {
        if (!purchReqLineRFQHelpTextMap.exists(purchReqLine.RecId))
        {
            purchReqLineRFQHelpTextMap.insert(purchReqLine.RecId, PurchReqControlRFQRule::getRFQHelpText(purchReqLine, purchReqLine.rfqRequirement == PurchReqRFQRequirement::Formal));
        }
        vendor_RFQRequirement.helpText(purchReqLineRFQHelpTextMap.lookup(purchReqLine.RecId));
    }

    assetRules = purchReqLine.findAndProcessRule();

    if (conLen(assetRules) > 0)
    {
        hasQualifier = conLen(conPeek(assetRules, qualifierPos)) > 0;
        selectedQualifierText.label(conPeek(assetRules, QualifierTextPos));

        if (!hasQualifier && !purchReqLine.ProjId && !purchReqLine.AssetGroup)
        {
            element.setAssetGroupAccess();
        }

        element.setLineAssetValueAccess(hasQualifier, hasQualifier);
    }
    else
    {
        hasQualifier = false;
        element.setAssetGroupAccess();
        element.setLineAssetValueAccess(false);
    }

    purchReqLine_ds.object(fieldNum(PurchReqLine, TransDate)).visible(PurchReqTableForm::isTransDateVisibleForLine(purchReqLine));

    // <GIN>
    if (isIndiaTaxesEnabled)
    {
        purchReqLine_ds.object(fieldNum(PurchReqLineTaxExtensionIN, MaximumRetailPrice)).enabled(((purchReqLine.LineType != PurchReqLineType::Item) &&
                                                                                       TaxParameters::checkTaxParameters_IN()));
    }
    // </GIN>

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        enableCFOPCFPSFields = purchReqLine.VendAccount != '';
        purchReqLine_ds.object(fieldNum(PurchReqLine,cfopTable_BR)).allowEdit(enableCFOPCFPSFields);
        purchReqLine_ds.object(fieldNum(PurchReqLine,cfpsTable_BR)).allowEdit(enableCFOPCFPSFields);
        purchReqLine_ds.object(fieldNum(PurchReqLine,SalesPurchOperationType_BR)).allowEdit(enableCFOPCFPSFields);

        if (!enableCFOPCFPSFields)
        {
            purchReqLine.cfopTable_BR = 0;
            purchReqLine.cfpsTable_BR = 0;
            purchReqLine.SalesPurchOperationType_BR = 0;
        }
        else
        {
            if (!purchReqLine.cfopTable_BR)
            {
                cfopValidation = CFOPValidation_BR::construct(purchReqLine);
                purchReqLine.cfopTable_BR = cfopValidation.getDefaultCFOP();
            }
        }

        element.enableServiceFields_BR();
    }
    // </GBR>

    element.refreshFormPart(formstr(PurchReqLineReviewDetails)); //object in menu item PurchReqLineReviewDetailsFormPart

    element.updateWorkflowButtonForLineVisibility();
    element.setVisible_Tax();
    purchReqTableForm.enableBudgetReservationFields(purchReqLine);

    this.disableFixedAssetTab();

    return ret;
}

public void create(boolean _append = true)
{
    InventSiteId     localInventSiteId;
    InventLocationId localInventLocationId;

    if (allowCreateReqLines && purchReqTable.RecId && purchReqTable.checkUpdate())
    {
        localInventSiteId               = inventDim.InventSiteId;
        localInventLocationId           = inventDim.InventLocationId;
        currentRequisitioner            = purchReqLine.Requisitioner;
        currentBuyingLegalEntity        = purchReqLine.BuyingLegalEntity;
        currentReceivingOperatingUnit   = purchReqLine.ReceivingOperatingUnit;

        super(_append);

        // Don't create new purchase line if current one was not created correctly
        if (!purchReqLine.ItemId)
        {
            purchReqTableForm.purchReqLine_Create(purchReqLine);
            if (!purchReqLine.isConsumption())
            {
                currentInventDim.InventSiteId     = localInventSiteId;
                currentInventDim.InventLocationId = localInventLocationId;
            }
        }

        element.setVisible_Tax();
    }
}

public void initValue()
{
    if (purchReqTable)
    {
        if (currentRequisitioner)
        {
            purchReqLine.initFromPurchReqTable(purchReqTable);
            purchReqLine.Requisitioner          = currentRequisitioner;
            purchReqLine.BuyingLegalEntity      = currentBuyingLegalEntity;
            purchReqLine.ReceivingOperatingUnit = currentReceivingOperatingUnit;
        }
        else
        {
            purchReqLine.Requisitioner          = purchReqTable.Originator;
            this.initInternalOrganizationValues();
            purchReqLine.initFromPurchReqTable(purchReqTable);
        }
    }

    purchReqLine.LineType = PurchReqLineType::Category;
    purchReqLine.modifiedField(fieldNum(PurchReqLine, Requisitioner));

    element.updateDefaultDimension();

    super();
}

public boolean validateWrite()
{
    boolean ret;

    purchReqLine.InventDimId         = InventDim::findOrCreate(inventDim).InventDimId;
    purchReqLine.setDataAreaForModifiedField(fieldNum(PurchReqLine, InventDimId));

    ret = super();

    return ret;
}

public void write()
{
    boolean isNew;

    purchReqLine.InventDimId            = InventDim::findOrCreate(inventDim).InventDimId;

    isNew = (purchReqLine.RecId == 0);

    super();

    if (purchReqLine.InventDimId != inventDim.InventDimId)
    {
        inventDim.data(InventDim::find(purchReqLine.InventDimId));
        inventDim_ds.setCurrent();
    }

    purchReqTable_ds.reread();
    purchReqTable_ds.refresh();

    element.updateWorkflowButtonEnabled();
    element.updateWorkflowButtonForLineVisibility();

    purchReqLine.performBudgetCheck();

    if (Tax::isTaxIntegrationEnabledForBusinessProcess(TaxIntegrationBusinessProcess::Purchase))
    {
        if (TaxIntegrationREQUpdateConflictFlight::instance().isEnabled())
        {
            purchReqTable_ds.reread();
            purchReqTable_ds.rereadLines();
        }
        else
        {
            PurchReqLine_ds.reread();
            PurchReqLine_ds.refresh();
        }
    }

    this.updateBudgetCheckResultDisplay();
}

/// <summary>
/// Updates budget check result display cache.
/// </summary>
public void updateBudgetCheckResultDisplay()
{
    this.cacheCalculateMethod(tableMethodStr(PurchReqLine, displayPurchReqProcessingResult));
}

public void clearAssetQualifier()
{
    purchReqLine.AssetRuleQualifierOption = 0;
    purchReqLine.AssetRuleQualifierOptionLocal = 0;
}

public display container workflowIcon(PurchReqLine _line)
{
    ImageReference imgRef;

    workflowIconObservableLink.observe();

    if (Workflow::findWorkItemsForCurrentUser(_line).elements())
    {
        imgRef = ImageReference::constructForSymbol(ImageReferenceSymbol::Workflow);
    }
    else
    {
        imgRef = ImageReference::constructForSymbol('');
    }

    return imgRef.pack();
}

public void executeQuery()
{
    XDSServices services;
    str prevXdsContext;
    boolean accessToPurchReqTableAll = PurchReqTableHcmWorkersPolicyHandler::checkCurrentUserAccessToPurchReqTableAll();

    try
    {
        if (!accessToPurchReqTableAll)
        {
            services = new XDSServices();
            prevXdsContext = PurchReqTableHcmWorkersPolicyHandler::getCurrentXdsContextString(services);
            PurchReqTableHcmWorkersPolicyHandler::setCurrentXdsContextString(services);
        }
        super();
        element.setLineAccess();
    }
    finally
    {
        if (!accessToPurchReqTableAll)
        {
            PurchReqTableHcmWorkersPolicyHandler::setCurrentXdsContextString(services, prevXdsContext);
        }

    }
}

private void disableFixedAssetTab()
{
    AssetParameters assertParams = AssetParameters::find();
    boolean disableFixedAssetsFileds = assertParams.AssetAllowAutoCreate == NoYes::No;

    if (disableFixedAssetsFileds)
    {
        tabFixedAsset.enabled(!disableFixedAssetsFileds);
    }
}

