    }
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (BrazilParameters::isEnabled())
    {
        ret = ret && CFOPValidation_BR::validateDeliveryAddress(purchReqLine.inventDim().inventSite().fiscalEstablishment(), purchReqLine.DeliveryPostalAddress);
    }

    return ret;
}

