public void modified()
{
    super();
    purchReqLine.modifyInventDim(inventDim,fieldNum(InventDim, InventLocationId));
    purchReqLine.setAddressFromInventDim(inventDim);
    purchReqLine.recalculateRequiredDate();

    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        LogisticsLocationRecId logisticsLocationRecId = TaxLogisticsLocationControllerHelper_IN::getDefaultLogisticsLocation(inventDim.InventLocationId, inventDim.InventSiteId);
        LogisticsPostalAddress logisticsAddress = LogisticsPostalAddress::findByLocation(logisticsLocationRecId);

        TransTaxInformationHelper::newHelper().updateCompanyLocationFromTransTable(purchReqLine, logisticsAddress.RecId);
    }
}

public boolean validate()
{
    boolean ret = true;

    if (purchReqLine.ItemId)
    {
        if (PdsGlobal::pdsIsCWItem(purchReqLine.ItemId))
        {
            return checkFailed(strFmt("@SCM:ErrorCatchWeightItemNotAllowedOnRequisitionLine", purchReqLine.ItemId));
        }

        ret = super();
    }

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    PurchReqLine::lookupItemId(_formControl, purchReqLine.BuyingLegalEntity, purchReqLine.ReceivingOperatingUnit, purchReqLine.RequiredDate
    ,purchReqLine.RequisitionPurpose
    );
}

public void modified()
{
    // <GBR>
    boolean enableCFOPCFPSFields;
    // </GBR>
    PurchReqRequesterRefRecId requester;

    if (!purchReqLine.ItemId)
    {
        requester               = purchReqLine.Requisitioner;
        buyingLE                = purchReqLine.BuyingLegalEntity;
        receivingOperatingUnit  = purchReqLine.ReceivingOperatingUnit;

        purchReqTableForm.resetPurchReqLine(purchReqLine);

        purchReqLine.Requisitioner          = requester;
        purchReqLine.BuyingLegalEntity      = buyingLE;
        purchReqLine.ReceivingOperatingUnit = receivingOperatingUnit;

        purchReqLine_ds.resetLine();
    }
    else
    {
        purchReqLine.LineType = PurchReqLineType::Item;
        if (!purchReqLine.isConsumption() && currentBuyingLegalEntity == purchReqLine.BuyingLegalEntity)
        {
            purchReqLine.InventDimId = InventDim::findOrCreate(currentInventDim).InventDimId;
        }

        super();

        inventDim.data(InventDim::find(purchReqLine.InventDimId));

        if (purchReqLine.ProjId)
        {
            element.projInventJournalTransMapForm().fieldModifiedItemIdPost();
        }

        if (purchReqLine.CurrencyCode != purchReqLine.orig().CurrencyCode && purchReqLine.orig().CurrencyCode)
        {
            purchReqLine_ds.write();
        }

        element.setDistributionTemplateAccess();
        element.updateDefaultDimension();
        element.updateDesign(InventDimFormDesignUpdate::FieldChange);
        // <GBR>
        if (BrazilParameters::isEnabled())
        {
            enableCFOPCFPSFields = purchReqLine.VendAccount != '';
            purchReqLine_ds.object(fieldNum(PurchReqLine,cfopTable_BR)).allowEdit(enableCFOPCFPSFields);
            purchReqLine_ds.object(fieldNum(PurchReqLine,cfpsTable_BR)).allowEdit(enableCFOPCFPSFields);
            purchReqLine_ds.object(fieldNum(PurchReqLine,SalesPurchOperationType_BR)).allowEdit(enableCFOPCFPSFields);

            if (!enableCFOPCFPSFields)
            {
                purchReqLine.cfopTable_BR = 0;
                purchReqLine.cfpsTable_BR = 0;
                purchReqLine.SalesPurchOperationType_BR = 0;
            }

            element.enableServiceFields_BR();
        }
        // </GBR>

        purchReqLine_ds.cacheCalculateMethod(tableMethodStr(PurchReqLine, editItemName));
        purchReqLine_ds.cacheCalculateMethod(tableMethodStr(PurchReqLine, vendName));

        purchReqLine_ds.rereadReferenceDataSources();
    }
    element.setLineAccess();
}

