    {
        purchReqLine_ds.rereadReferenceDataSources();
    }
    else
    {
        purchReqLine.LineType = PurchReqLineType::Category;
        purchReqLine.CurrencyCode = Ledger::accountingCurrency();

        super();
    }

    element.setFieldAccess();

    if (purchReqLine.ProjId)
    {
        element.projInventJournalTransMapForm().fieldModifiedProcurementCategoryPost();
    }
}

