    {
        element.setFieldAccess();
        element.projInventJournalTransMapForm().fieldModifiedProjCategoryId();
    }
}

public void modified()
{
    super();

    if (!purchReqLine.ProjId)
    {
        purchReqLine.updateAssetGroup();
        element.setAssetGroupAccess();
    }

    if (purchReqLine.ProjId)
    {
        PurchReqLine.AccountingDistributionTemplate = 0;
        purchReqLine.initFromProjTable(ProjTable::find(purchReqLine.ProjId));
        purchReqLine_ds.refresh();

        //Make sure the reference bound field is updated with the new value
        projectSalesPrice_ProjSalesUnitOfMeasure.value(purchReqLine.ProjSalesUnitOfMeasure);

        purchReqLine_ds.rereadReferenceDataSources();
    }

    element.setDistributionTemplateAccess();
    element.updateDefaultDimension();
}

