public void modified()
{
    purchReqLine_ds.initInternalOrganizationValues();

    super();

    element.updateDefaultDimension();
}

public str toolTip()
{
    str ret;

    if (purchReqLine.rfqRequirement != PurchReqRFQRequirement::None)
    {
        if (!purchReqLineRFQHelpTextMap.exists(purchReqLine.RecId))
        {
            purchReqLineRFQHelpTextMap.insert(purchReqLine.RecId, PurchReqControlRFQRule::getRFQHelpText(purchReqLine, purchReqLine.rfqRequirement == PurchReqRFQRequirement::Formal));
        }

        ret = purchReqLineRFQHelpTextMap.lookup(purchReqLine.RecId);
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    return salesPurchOperationTypeController_BR.lookupReference(_formReferenceControl);
}

public boolean validate()
{
    boolean ret;

    ret = salesPurchOperationTypeController_BR.validate(purchReqLine.SalesPurchOperationType_BR);

    return super() && ret;
}

