}

public void lookup(FormControl _formControl, str _filterStr)
{
    LogisticsPostalAddress  localLogisticsPostalAddress;

    localLogisticsPostalAddress = LogisticsLocationEntity::location2PostalAddress(purchReqLine.deliveryLocation());

    TaxServiceCode_BR::lookup(_formControl, NoYes::No,
                              logisticsPostalAddress.CountryRegionId,
                              logisticsPostalAddress.State,
                              logisticsPostalAddress.City);
}

