    LineDimensionEntryControl.parmActiveValueFilterDate(purchReqLine.TransDate);
}

public void modified()
{
    // <GBR>
    boolean enableCFOPCFPSFields;
    // </GBR>
    super();
    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        enableCFOPCFPSFields = purchReqLine.VendAccount != '';
        purchReqLine_ds.object(fieldNum(PurchReqLine,cfopTable_BR)).allowEdit(enableCFOPCFPSFields);
        purchReqLine_ds.object(fieldNum(PurchReqLine,cfpsTable_BR)).allowEdit(enableCFOPCFPSFields);
        purchReqLine_ds.object(fieldNum(PurchReqLine,SalesPurchOperationType_BR)).allowEdit(enableCFOPCFPSFields);
    }
    // </GBR>

    element.updateDefaultDimension();

    if (!PurchReqLineSkipWriteWhenModifyVendAccountFlight::instance().isEnabled())
    {
        purchReqLine_ds.write();
        purchReqLine_ds.reread();
    }
    else if (PurchReqLineVendAccountModifiedNameUpdatedFlight::instance().isEnabled())
    {
        purchReqLine_ds.cacheCalculateMethod(tableMethodStr(PurchReqLine, vendName));
    }
}

