internal void modifyCustomInventDimension(FieldId _fieldId)
{
    if (InventDim::isFieldIdProductDimension(_fieldId))
    {
        this.modifyInventDimField(_fieldId);
        purchReqLine_ds.cacheCalculateMethod(tableMethodStr(PurchReqLine, itemName));
    }
    else if (InventDim::isFieldIdTrackingDimension(_fieldId))
    {
        purchReqLine.modifyInventDim(inventDim, _fieldId);
    }
}

internal void modifyInventDimField(FieldId _fieldId)
{
    if (purchReqLine.modifyInventDim(inventDim, _fieldId))
    {
        InventDim.data(purchReqLine.inventDim());
    }
}

