public void executeQuery()
{
    RecId           currentWorker;

    if (isQuestionnaireTabActive)
    {
        super();

        // Only a Preparer (Originator) can modify a line in a PR created by them.
        currentWorker = HcmWorker::findByPerson(DirPersonUser::current().PersonParty, false, ConcurrencyModel::Auto).RecId;

        // Enable the button if a questionnaire exists for the line item and
        // the current user (worker) prepared the PR.
        buttonGroupQuestionnaire.enabled(purchReqLineKMCollection.RecId);
        buttonCompleteQuestionnaire.enabled(currentWorker == purchReqTable.Originator);
    }
}

